import React from "react";
import { connect } from "react-redux";
import { injectIntl } from "react-intl";
import * as blockActions from "../../../../../../Store/Reducer/block";
import { getColorTheme } from "../../../../../../utils/colorThemeUtil";
import "./index.scss";

const PositionEditor = props => {
  const {
    intl,
    defaultX,
    defaultY,
    popupStyle,
    onClickCancel,
    onClickConfirm,
    setPopupConstantValue
  } = props;
  const { formatMessage } = intl;
  const darkmode = getColorTheme() === "darkMode";

  const numpads = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0, ".", "-", "delete"];
  const onClickNumpad = numpad => {
    const input = document.getElementsByClassName(
      "oobceditor_popup_input-selected"
    )[0];
    switch (numpad) {
      case ".":
        if (input.value === "") {
          input.value = "0.";
        } else if (input.value.indexOf(numpad) < 0) {
          input.value = input.value + numpad;
        }
        break;
      case "-":
        if (input.value.indexOf(numpad) < 0) {
          input.value = numpad + input.value;
        } else {
          input.value = input.value.slice(1, input.value.length);
        }
        break;
      case "delete":
        if (input.value === "0.") {
          input.value = "";
        } else {
          input.value = input.value.slice(0, input.value.length - 1);
        }
        break;
      default:
        if (input.value === "0") {
          input.value = numpad;
        } else {
          input.value = input.value + numpad;
        }
        break;
    }
    const inputX = document.getElementById("oobceditor_popup_inputX");
    const inputY = document.getElementById("oobceditor_popup_inputY");
    if (parseFloat(inputX.value) >= 10000) inputX.value = 9999;
    if (parseFloat(inputY.value) >= 10000) inputY.value = 9999;
    setPopupConstantValue({
      x: parseFloat(inputX.value) || 0,
      y: parseFloat(inputY.value) || 0
    });
  };
  const onClickInput = e => {
    const inputX = document.getElementById("oobceditor_popup_inputX");
    const inputY = document.getElementById("oobceditor_popup_inputY");
    inputX.classList.remove("oobceditor_popup_input-selected");
    inputY.classList.remove("oobceditor_popup_input-selected");
    e.currentTarget.children[1].classList.add(
      "oobceditor_popup_input-selected"
    );
  };
  const onClickClear = () => {
    const input = document.getElementsByClassName(
      "oobceditor_popup_input-selected"
    )[0];
    input.value = "";
    const inputX = document.getElementById("oobceditor_popup_inputX");
    const inputY = document.getElementById("oobceditor_popup_inputY");
    setPopupConstantValue({
      x: parseFloat(inputX.value),
      y: parseFloat(inputY.value)
    });
  };

  return (
    <div
      id="oobceditor_popup_container"
      className="oobceditor_popup_container oobceditor_popup_container-position"
      style={popupStyle}
    >
      <div className="oobceditor_popup_inputs">
        <div className="oobceditor_popup_input_wrapper" onClick={onClickInput}>
          <div className="oobceditor_popup_input_title">X :</div>
          <input
            id="oobceditor_popup_inputX"
            className="oobceditor_popup_input oobceditor_popup_input-selected"
            defaultValue={`${defaultX}`}
          />
        </div>
        <div className="oobceditor_popup_input_wrapper" onClick={onClickInput}>
          <div className="oobceditor_popup_input_title">Y :</div>
          <input
            id="oobceditor_popup_inputY"
            className="oobceditor_popup_input"
            defaultValue={`${defaultY}`}
            onClick={onClickInput}
          />
        </div>
      </div>
      <div className="oobceditor_popup_numpads">
        {numpads.map(numpad => (
          <div
            key={numpad}
            className={`oobceditor_popup_numpad oobceditor_popup_numpad-${numpad}`}
            onClick={() => {
              onClickNumpad(numpad);
            }}
            onTouchStart={e => {
              e.currentTarget.classList.add("oobceditor_popup_numpad-touched");
            }}
            onTouchEnd={e => {
              e.currentTarget.classList.remove(
                "oobceditor_popup_numpad-touched"
              );
            }}
          >
            {numpad === "delete" ? (
              <img
                src={
                  darkmode
                    ? require("../../../../../../../../Image/builder/oobceditor/number_delete-white.png")
                    : require("../../../../../../../../Image/builder/oobceditor/number_delete-black.png")
                }
                alt={numpad}
              />
            ) : (
              numpad
            )}
          </div>
        ))}
      </div>
      <div className="oobceditor_popup_buttons">
        <button
          id="oobceditor_popup_button-clear"
          className="oobceditor_popup_button oobceditor_popup_button-clear"
          onClick={() => {
            onClickClear();
          }}
          onTouchStart={e => {
            e.currentTarget.classList.add("oobceditor_popup_button-touched");
          }}
          onTouchEnd={e => {
            e.currentTarget.classList.remove("oobceditor_popup_button-touched");
          }}
        >
          {formatMessage({ id: "ID_OOBC_EDITOR_CLEAR" })}
        </button>
        <button
          id="oobceditor_popup_button-cancel"
          className="oobceditor_popup_button oobceditor_popup_button-cancel"
          onClick={() => {
            onClickCancel();
          }}
          onTouchStart={e => {
            e.currentTarget.classList.add("oobceditor_popup_button-touched");
          }}
          onTouchEnd={e => {
            e.currentTarget.classList.remove("oobceditor_popup_button-touched");
          }}
        >
          {formatMessage({ id: "ID_OOBC_EDITOR_CANCEL" })}
        </button>
        <button
          id="oobceditor_popup_button-confirm"
          className="oobceditor_popup_button oobceditor_popup_button-confirm"
          onClick={() => {
            const inputX = document.getElementById("oobceditor_popup_inputX");
            const inputY = document.getElementById("oobceditor_popup_inputY");
            const valueX = parseFloat(inputX.value) || 0;
            const valueY = parseFloat(inputY.value) || 0;
            onClickConfirm(valueX, valueY);
          }}
          onTouchStart={e => {
            e.currentTarget.classList.add("oobceditor_popup_button-touched");
          }}
          onTouchEnd={e => {
            e.currentTarget.classList.remove("oobceditor_popup_button-touched");
          }}
        >
          {formatMessage({ id: "ID_OOBC_EDITOR_CONFIRM" })}
        </button>
      </div>
    </div>
  );
};

export default connect(
  undefined,
  {
    setPopupConstantValue: blockActions.setPopupConstantValue
  }
)(injectIntl(PositionEditor));
