import React from 'react';
import { injectIntl } from "react-intl";
import "./index.scss";
import Field from "../../../Common/Component/Field";

function View(props) {
  const {
    intl,
    allValid,
    onChangeField,
    onClickChange,
    currentPassword,
    password,
    passwordRe,
    isPasswordLengthValid,
    isPasswordCombValid,
    warnings
  } = props;
  return (
    <div className="change-pw">
      <Field.SecureText
        id="currentPassword"
        value={currentPassword}
        onChange={onChangeField}
        title={intl.formatMessage({id: "ID_MY_ACCOUNT_PW_CURRENT"})}
        placeholder={intl.formatMessage({id: "ID_SIGNIN_PW_PLACEHOLDER"})}
        secureToggleOption={true}
        warning={warnings.currentPassword}
      />
      <Field.SecureText
        id="password"
        value={password}
        onChange={onChangeField}
        title={intl.formatMessage({id: "ID_MY_ACCOUNT_PW_NEW"})}
        placeholder={intl.formatMessage({id: "ID_SIGNIN_PW_PLACEHOLDER"})}
        secureToggleOption={true}
        informations={[
          {
            text: intl.formatMessage({ id: "ID_SIGNUP_PW_INFO_LENGTH"}),
            valid: password.length ? isPasswordLengthValid : undefined
          },
          {
            text: intl.formatMessage({ id: "ID_SIGNUP_PW_INFO_MIX"}),
            valid: password.length ? isPasswordCombValid : undefined
          }
        ]}
      />
      <Field.SecureText
        id="passwordRe"
        value={passwordRe}
        onChange={onChangeField}
        title={intl.formatMessage({id: "ID_MY_ACCOUNT_PW_RE"})}
        placeholder={intl.formatMessage({id: "ID_SIGNIN_PW_PLACEHOLDER"})}
        secureToggleOption={true}
        warning={warnings.passwordRe}
      />
      <button className={`change-pw__button enable--${allValid}`} onClick={onClickChange}>
        {intl.formatMessage({id: "ID_MY_ACCOUNT_TAB_CHANGE_PW"})}
      </button>
    </div>
  );
}

export default injectIntl(View);
