import React from "react";
import QueryString from "query-string";
import { withRouter } from "react-router-dom";
import WizAppDetail from "../Component/WizAppDetail";

function WizAppModalProvider(props) {
  const { pId, isReview } = QueryString.parse(props.location.search);
  return (
    <>
      {props.children}
      {pId && <WizAppDetail pId={pId} isReview={isReview}/>}
    </>
  )
}

export default withRouter(WizAppModalProvider);
