import React from "react";
import Slider from "react-slick";
import { Link } from "react-router-dom";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./index.scss";

export default function View(props) {
  const {
    setSlickRef,
    handlePrev,
    handleNext,
    news,
    currentSlideIndex,
    onChangeIndex
  } = props;

  const bannerSlidSettings = {
    speed: 500,
    autoplaySpeed: 5000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    infinite: true,
    arrows: false,
    beforeChange: (_, next) => onChangeIndex(next)
  };

  return (
    news.length > 0 && (
      <div className="main__news">
        <div className="main__title">뉴스</div>
        <div className="newsItem__content__slide">
          <div className="newsItem__content__slide__controls">
            <div className="newsItem__content__slide__page">
              {currentSlideIndex + 1}/{news.length}
            </div>
            <div className="newsLeftIcon" onClick={handlePrev} />
            <div className="newsRightIcon" onClick={handleNext} />
          </div>
        </div>
        <div className="news__items">
          <Slider {...bannerSlidSettings} ref={setSlickRef}>
            {news.map((item, index) => {
              return (
                <Link
                  to={`/news/detail/${item.id}`}
                  target="_blank"
                  className="newsItem__link"
                  key={index}
                >
                  <div className="newsItem">
                    <img
                      className="newsItem__img"
                      src={item.thumbnail}
                      alt=""
                    />
                    <div className="newsItem__content">
                      <div className="newsItem__content__topic">{item.topic}</div>
                      <div className="newsItem__content__title">{item.title}</div>
                      <div className="newsItem__content__subTitle">{item.subtitle}</div>
                    </div>
                  </div>
                </Link>
              );
            })}
          </Slider>
        </div>
      </div>
    )
  );
}
