import React from "react";
import { connect } from "react-redux";
import { injectIntl } from "react-intl";
import * as blockActions from "../../../../../../Store/Reducer/block";
import "./index.scss";

const StringEditor = props => {
  const {
    intl,
    defaultValue,
    popupStyle,
    onClickCancel,
    onClickConfirm,
    setPopupConstantValue
  } = props;
  const { formatMessage } = intl;

  const onClickClear = () => {
    const input = document.getElementById("oobceditor_popup_input");
    input.value = "";
    setPopupConstantValue(input.value);
    input.focus();
    const confirmButton = document.getElementById(
      "oobceditor_popup_button-confirm"
    );
    confirmButton.classList.add("oobceditor_popup_button-disabled");
  };

  return (
    <div
      id="oobceditor_popup_container"
      className={`oobceditor_popup_container oobceditor_popup_container-string${
        window.webkit ? " oobceditor_popup_container-webkit" : ""
      }`}
      style={popupStyle}
    >
      <input
        id="oobceditor_popup_input"
        className="oobceditor_popup_input"
        defaultValue={defaultValue}
        onFocus={e => {
          e.currentTarget.select();
        }}
        onChange={e => {
          const confirmButton = document.getElementById(
            "oobceditor_popup_button-confirm"
          );
          const value = e.currentTarget.value;
          if (value === "") {
            confirmButton.classList.add("oobceditor_popup_button-disabled");
          } else {
            confirmButton.classList.remove("oobceditor_popup_button-disabled");
          }
        }}
        autoFocus
      />
      <div className="oobceditor_popup_buttons">
        <button
          id="oobceditor_popup_button-clear"
          className="oobceditor_popup_button oobceditor_popup_button-clear"
          onClick={() => {
            onClickClear();
          }}
          onTouchStart={e => {
            e.currentTarget.classList.add("oobceditor_popup_button-touched");
          }}
          onTouchEnd={e => {
            e.currentTarget.classList.remove("oobceditor_popup_button-touched");
          }}
        >
          {formatMessage({ id: "ID_OOBC_EDITOR_CLEAR" })}
        </button>
        <button
          id="oobceditor_popup_button-cancel"
          className="oobceditor_popup_button oobceditor_popup_button-cancel"
          onClick={() => {
            onClickCancel();
          }}
          onTouchStart={e => {
            e.currentTarget.classList.add("oobceditor_popup_button-touched");
          }}
          onTouchEnd={e => {
            e.currentTarget.classList.remove("oobceditor_popup_button-touched");
          }}
        >
          {formatMessage({ id: "ID_OOBC_EDITOR_CANCEL" })}
        </button>
        <button
          id="oobceditor_popup_button-confirm"
          className="oobceditor_popup_button oobceditor_popup_button-confirm"
          onClick={() => {
            const input = document.getElementById("oobceditor_popup_input");
            const value = input.value;

            onClickConfirm(value);
          }}
          onTouchStart={e => {
            e.currentTarget.classList.add("oobceditor_popup_button-touched");
          }}
          onTouchEnd={e => {
            e.currentTarget.classList.remove("oobceditor_popup_button-touched");
          }}
        >
          {formatMessage({ id: "ID_OOBC_EDITOR_CONFIRM" })}
        </button>
      </div>
    </div>
  );
};

export default connect(
  undefined,
  {
    setPopupConstantValue: blockActions.setPopupConstantValue
  }
)(injectIntl(StringEditor));
