import React from "react";
import "./index.scss";
import FilteringHeader from "../FilteringHeader";
import projectDetailIcon from "../../../../../../Image/icon-more.svg";
import editIcon from "../../../../../../Image/icon-edit.svg";
import copyIcon from "../../../../../../Image/copy.svg";
import deleteIcon from "../../../../../../Image/delete.svg";
import project3DLabel from "../../../../../../Image/3d-label.svg";
import { is3DProject } from "../../../../Container";

export default function View(props) {
  const {
    setFilteringData,
    myProjects,
    onClickProject,
    handleOnScroll,
    projectsRef,
    handleEdit,
    onClickDetailBtn,
    selectProject,
    handleDelete,
    handleCopy,
    setProjectName
  } = props;

  const getTypeFromIconURL = item => {
    return item.icon.includes("redbrick") ? "3D" : item.type;
  };

  return (
    <div
      className="builder--home__myProject"
      ref={projectsRef}
      onScroll={handleOnScroll}
    >
      <p className="myProject__title">최근 프로젝트</p>
      <FilteringHeader setFilteringData={setFilteringData} />
      <div className="bottom__projectItems">
        {myProjects.length ? (
          myProjects.map((item, index) => {
            return (
              <div className="projectItem" key={index}>
                <div
                  className="projectItem__top"
                  onClick={e => {
                    e.preventDefault();
                    onClickProject(item.pId, getTypeFromIconURL(item));
                  }}
                >
                  <img className="top__img" src={item.icon} alt="icon" />
                  {is3DProject(item) && <img className="projectItem__3d-label" src={project3DLabel} alt="3d-label" />}
                </div>

                <div
                  className="projectItem__bottom"
                  onClick={e => {
                    e.preventDefault();
                    onClickProject(item.pId, getTypeFromIconURL(item));
                  }}
                >
                  <div className="bottom__title">{item.name}</div>
                  <div className="bottom__time">
                    {item.updatedAt &&
                      item.updatedAt.split("T")[0].replaceAll("-", ".")}
                  </div>
                </div>
                <div
                  className={`projectItem__detail ${selectProject.pId ===
                    item.pId &&
                    selectProject.type !== "published" &&
                    "selected"}`}
                  onClick={() => {
                    onClickDetailBtn(item.pId);
                  }}
                >
                  <img src={projectDetailIcon} alt="project Detail Icon" />
                  <ul className="projectItem__detail__list">
                    <li
                      onClick={() =>
                        handleEdit(item.pId, item.name, setProjectName)
                      }
                    >
                      <img src={editIcon} alt="" />
                      수정
                    </li>
                    <li onClick={() => handleCopy(item.pId, setFilteringData)}>
                      <img src={copyIcon} alt="" />
                      복사
                    </li>
                    <li
                      onClick={() => handleDelete(item.pId, setFilteringData)}
                    >
                      <img src={deleteIcon} alt="" />
                      삭제
                    </li>
                  </ul>
                </div>
              </div>
            );
          })
        ) : (
          <div className="project__Items no__Items">프로젝트가 없습니다.</div>
        )}
      </div>
    </div>
  );
}
