import React, { Component } from "react";
import * as request from "../../../../../Common/Util/HTTPRequest";
import View from "./View";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hofList: []
    }
  }
  componentDidMount() {
    this.loadHOFList();
  }
  loadHOFList() {
    request
      .getCreatorChallengeHallOfFames()
      .then(res => res.json())
      .then(hofList => {
        this.setState({ hofList });
      })
  }

  render() {
    return (
      <View 
        {...this.state}
      />
    );
  }
}

export default Container;
