import React, { useCallback, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { connect } from "react-redux"; import styled from "@emotion/styled";
import { postQna } from "../api";
import * as request from "../../../Common/Util/HTTPRequest";
import * as Popup from "../../../Common/Component/PopUp";

import Button from "../Components/Button";

import { COLOR } from "./../Constants";
import { IMAGE } from "./../Constants/Images";
import AlertPopup from "../Components/AlertPopup"

const Self = styled.div`
  flex: 1; 

  @media screen and (max-width: 1169px){
    width: 88.33vw;
    margin: 0 auto;
  }
`;

const Title = styled.h2`
  font-size: 22px;
  font-weight: bold;
  line-height: 1.45;
  text-align: center;
  color: #fff;
  padding-top: 28px;
  padding-bottom: 19.5px;
  border-bottom: solid 1px rgba(255, 255, 255, 0.2);
  margin-bottom: 19.5px;
  position: relative;

  @media screen and (max-width: 1169px){
    padding-top: 0;
  }
`

const PageBack = styled.div`
  width: 32px;
  height: 32px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  background-image: url(${IMAGE.ICON_BACK});
  position: absolute;
  left: 0;
  bottom: 19.5px;
  cursor: pointer;
`

const Form = styled.form``;

const FormGroup = styled.div`
  display: flex;
  margin-bottom: 20px;

  ${(props) => props.type === "imageUpload" && `
    flex-direction: column;
  `}
`

const TitleInput = styled.input`
  width: 100%;
  padding: 16px 16px 14px;
  border-radius: 10px;
  background-color: #1c1c1c;
  border: none;
  font-size: 16px;
  line-height: 1.38;
  color: #fff;

  &:focus {
    outline: none;
  }

  &::placeholder {
    color: #9e9e9e;
  }
`;

const TypeSelect = styled.div`
  width: 100%;
  height: 52px;
