const File = props => {
  const { id, accept, values, onChange } = props;
  const fileInputRef = useRef();
  return (
    <div>
      <input
        id={id}
        ref={fileInputRef}
        type="file"
        accept={accept}
        onChange={e => {
          if (values.length === 4) {
            alert("최대 업로드 가능한 파일은 4개입니다.");
            return;
          }
          const selectedFile = e.target.files[0];
          if (!selectedFile) return;

          let size = formatBytes(selectedFile.size)

          const data = new FormData();
          data.append("file", selectedFile);
          request
            .dreamUpload(data)
            .then(res => res.json())
            .then(json => {
              if (json.success) {
                onChange([...values, { url: "/" + json.key, size }]);
              } else {
                throw json;
              }
            })
            .catch(err => {
              alert(JSON.stringify(err));
            });
        }}
        hidden
      />
      <InputWrap
        onClick={() => {
          fileInputRef.current.click();
        }}
      >
        <ImgIcon src={IMAGE.ICON_IMAGE} />
        <ButtonText>이미지 업로드</ButtonText>
      </InputWrap>
    </div>
  );
}

const QnaNew = ({ ...props }) => {
