  const handleSubmitPost = async () => {

    try {
      const result = await postQna({
        ...qnaForm,
        type: qnaForm.type.includes("기술") ? "tech" : "learning",
        userId: props.userId,
        imageJSON: images ? JSON.stringify(images) : null
