import React from "react";
import { Link } from "react-router-dom";
import Layout from "../../Common/Component/Layout";
import "./index.scss";

import playStoreIcon from "../../Image/google-play-badge@2x.png";

export default function View(props) {
  const { intl, recommendedGames } = props;
  return (
    <Layout>
      <div className="editor-recommend">
        <p className="editor-recommend__title">
          {intl.formatMessage({ id: "ID_HOME_EDITOR_TITLE" })}
        </p>
        <div className="editor-recommend__items">
          {recommendedGames.map((game, i) => {
            return (
              <div className="editor-recommend__item" key={i}>
                <Link to={`?pId=${game.pId}`}>
                  <img 
                    className="editor-recommend__item__image"
                    src={game.icon}
                    alt="editor-recommend-thumbnail"
                  />
                  <div className="editor-recommend__item__content">
                    <p className="editor-recommend__item__title">{game.project.name}</p>
                    <p className="editor-recommend__item__maker">{game.project.user.name}</p>
                    <p className="editor-recommend__item__info">{game.description}</p>
                  </div>
                </Link>
                {/* {game.playStoreURL && (
                  <a
                    href={game.playStoreURL}
                    className="editor-recommend__item__play-store-icon"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <img src={playStoreIcon} alt="playstore icon" />
                  </a>
                )} */}
              </div>
            );
          })}
        </div>
      </div>
    </Layout>
  );
}
