import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { injectIntl } from "react-intl";
import * as request from "../../Util/HTTPRequest";
import "./index.scss";

class FindPw extends Component {
  constructor(props) {
    super(props);
    this.state = { resend : false, warning : ""  }
  }

  onClickResend = () => {
    const { email, verifiedEmail } = this.props
    request
    .sendEmailResetPwd({ email: email , verifiedEmail: verifiedEmail })
    .then(res => res.json())
    .then(json => {
      const {success , statusCode} = json
      if(success) {
        this.setState({resend : true, warning: ""})
      } else {
        if(statusCode === 404) {
          this.setState({ resend: false, warning: this.props.intl.formatMessage({ id: "ID_VALIDATE_EMAIL_NOT_EXIST" }) });
        } else {
          this.setState({ resend: false, warning: this.props.intl.formatMessage({ id: "ID_REPORT_POPUP_FAIL_MSG" }) });
        }
      }
    })
  }

  render() {
    const { formatMessage } = this.props.intl;
    return (
      <div className="findpw-result">
        <div className="findpw-result__title">
          {formatMessage({ id: "ID_FORGOTPW_RESULT" })}
        </div>
        <div className="findpw-result__desc"> 
          <FormattedMessage id="ID_FIND_PW_RESULT_DESC" values={{verifiedEmail: this.props.verifiedEmail}} />
        </div>
        <div className="findpw-result__button" onClick={this.onClickResend}>
          <button className="findpw-result__button-send-mail">
            {formatMessage({ id: "ID_FIND_PW_SEND_EMAIL_RETRY" })}
          </button>
          {
            this.state.resend 
            ? <div className="findpw-result__button-retry-success">{formatMessage({ id: "ID_FIND_PW_SEND_EMAIL_RETRY_DESC" })}</div>
            : <div className="findpw-result__button-retry-fail">{this.state.warning}</div>
          }
        </div>
      </div>
    );
  }
}

export default injectIntl(FindPw);
