import React, { Component } from "react";
import { connect } from "react-redux";
import { FormattedMessage, injectIntl } from "react-intl";
import { Link, withRouter } from "react-router-dom";
import onClickOutside from "react-onclickoutside";
import * as userInfoActions from "../../../Store/Reducer/UserInfo";
import locales from "../../../../locale";
import UserIconWrapper from "../../UserIconWrapper";

import KRFlag from "../../../../Image/icon-kr.svg";
import USFlag from "../../../../Image/icon-us.svg"
import CNFlag from "../../../../Image/icon-ch.svg"
import GlobalImg from "../../../../Image/icon-newglobal.svg";
import "./index.scss";

const DDItem = ({ user, text, url, onClick, style }) => {
  if (user) {
    return (
      <div className="DDItem-user">
        <div className="DDItem-user__photo">
          <UserIconWrapper iconSrc={user.icon}/>
        </div>
        <div className="DDItem-user__name">
          {user.name}
        </div>
      </div>
    );
  }

  if (url) {
    return (
      <Link to={url} style={{ textDecoration: "none" }}>
        <div className={`DDItem`} style={style} onClick={onClick}>
          <div className="DDItem__text">{text}</div>
        </div>
      </Link>
    );
  }


  return (
    <div className={`DDItem`} onClick={onClick} style={style}>
      {text==="KR" &&  <img className="kr-flag" src={KRFlag} alt="kr"/>}
      {text==="CN" && <img className="cn-flag" src={CNFlag} alt="cn"/>}
      {text==="US" && <img className="us-flag" src={USFlag} alt="us" />}
      <div className="DDItem__text">{text}</div>
    </div>
  );
};

class DropDown extends Component {
  constructor(props) {
    super(props);
    this.languages = Object.keys(locales).map(locale => ({
      locale,
      current: locale === this.props.intl.locale,
      lang: locales[locale]["ID_HEADER_LANG"]
    }));
    this.state = {
      isListOpened: false,
      selectedItem: null
    };
  }
  handleClickOutside = e => {
    this.setState({
      isListOpened: false
    });
  };

  handleItemClick = item => {
    this.props.handleSelectItem(item.id);
    this.setState({ selectedItem: item, isListOpened: false });
  };

  handleToggleList = () => {
    this.setState(state => {
      return { isListOpened: !state.isListOpened };
    });
  };

  onChangeLanguage = language => {
    if (language.current) return;
    localStorage.setItem("wizLang", language.locale);
    window.location.reload();
  };

  onClickLogout = () => {
    localStorage.removeItem("wizToken");
    this.props.updateUserInfo();
  };

  render() {
    const { type, user } = this.props;
    let { isListOpened } = this.state;

    const style = {
      DDItem: type === "lang" ? { padding: 0 } : { padding: "8px 16px" },
    };

    return (
      <div
        className={`DD ${type === "lang" ? "DD__lang" : "DD__user"}`}
        style={style.DD}
      >
        <div className="DD__header" onClick={this.handleToggleList}>
          {type === "lang" ? (
            <span className="DDTagName">
              <img src={GlobalImg} alt="" width="24px" />
            </span>
          ) : (
            <div className="DD__header__user">
              <UserIconWrapper iconSrc={user.icon} />
            </div>
          )}
        </div>
        <div className="DD__body">
          {isListOpened ? (
            type === "lang" ? (
              <div className="DD__list--lang">
                {this.languages.map((language, index) => {
                  return (
                    <DDItem
                      style={language.current ? { fontWeight: "bold" } : {...style.DDItem}}
                      key={index}
                      text={language.lang}
                      onClick={() => {
                        this.onChangeLanguage(language);
                      }}
                    />
                  );
                })}
              </div>
            ) : (
              <div className="DD__list--user">
                <DDItem user={user} />
                <DDItem
                  style={{
                    ...style.DDItem
                  }}
                  text={<FormattedMessage id="ID_USERDD_MYPAGE" />}
                  url="/mypage"
                />
                <DDItem
                  style={{
                    ...style.DDItem
                  }}
                  text={<FormattedMessage id="ID_USERDD_MYAPK" />}
                  url="/myapk"
                />
                <DDItem
                  style={{
                    ...style.DDItem
                  }}
                  text={<FormattedMessage id="ID_USERDD_ACCOUNT" />}
                  url="/myaccount"
                />
                <DDItem
                  style={{
                    ...style.DDItem,
                    marginBottom: "24px"
                  }}
                  text={<FormattedMessage id="ID_USERDD_LOGOUT" />}
                  onClick={this.onClickLogout}
                />
              </div>
            )
          ) : (
            ""
          )}
        </div>
      </div>
    );
  }
}

export default connect(
  state => ({ userinfo: state.userinfo }),
  {
    updateUserInfo: userInfoActions.updateUserInfo
  }
)(injectIntl(withRouter(onClickOutside(DropDown))));
