import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import * as request from "../../../../../Common/Util/HTTPRequest";
import ChallengeProject from "../ChallengeProjectItem"
import searchIcon from "../../../../../Image/icon-search.svg";
import "./index.scss";

// temp challenge review
export default class View extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentTab: "new",
      projects: [],
      searchText: ""
    };
  }
  componentDidMount() {
    this.initProjects();
    window.addEventListener("scroll", this.onWindowScroll);
  }
  componentWillUnmount() {
    window.removeEventListener("scroll", this.onWindowScroll);
  }
  componentDidUpdate(prevProps, prevState) {
    if(prevState.currentTab !== this.state.currentTab) {
      this.didUpdateCurrentTab()
    }
  }
  didUpdateCurrentTab() {
    this.initProjects();
  }

  async initProjects() {
    this.isAllProjectsLoaded = false;
    const projects = await this.loadProjects({
      offset: 0,
      limit: 20,
      searchText : this.state.searchText
    });

    this.setState({ projects });
  }
  async loadMoreProjects() {
    const projects = await this.loadProjects({ 
      offset: this.state.projects.length,
      limit: 20,
      searchText : this.state.searchText
    });
    this.setState(prevState => ({ 
      projects: [].concat(prevState.projects, projects)
    }));
  }
  async loadProjects(options) {
    if(this.isLoadingProjects) {
      return;
    }

    this.isLoadingProjects = true;
    const projects = await request
      .getCreatorChallengesReviews({
        offset: options.offset,
        limit: options.limit,
        order: this.order,
        searchText : options.searchText
      })
      .then(res => res.json())
      .finally(() => {
        this.isLoadingProjects = false;
      });

    if(projects.length < options.limit) {
      this.isAllProjectsLoaded = true;
    }

    return projects;
  }
  get order() {
    const tabToOrder = {
      new: "createdAt",
      popular: "like"
    }
    return tabToOrder[this.state.currentTab];
  }

  onWindowScroll = e => {
    const target = document.documentElement;
    if(
      target.scrollTop >= target.scrollHeight - target.clientHeight - 100
      && !this.isAllProjectsLoaded
      && !this.isLoadingProjects
    ) {
      this.loadMoreProjects();
    }
  }

  onClickTab = (tab) => {
    this.setState({ currentTab : tab })
  }

  onChangeSearchText = e => {
    this.setState({ searchText: e.target.value });
  }

  onClickSearch = () => {
    this.initProjects();
  }

  render() {
    const { currentTab, projects } = this.state;
    const { onClickTab } = this;
    return (
      <div className="challenge__project">
        <div className="review__search-bar">
          <input
            className="review__search-bar__input"
            placeholder="검색어를 입력해주세요."
            value={this.state.searchText}
            onChange={this.onChangeSearchText}
            onKeyDown={e => {
              if(e.key === "Enter") {
                this.onClickSearch();
              }
            }}
          />
          <button
            className="review__search-bar__button"
            onClick={this.onClickSearch}
          >
            <img
              className="review__search-bar__icon"
              src={searchIcon}
              alt="search"
            />
          </button>
        </div>
        <div className="challenge__project__tabs">
          <div 
            className={`challenge__project__tab ${currentTab === "new" && "tab--active"}`}
            onClick={() => onClickTab("new")}
          >
            <FormattedMessage id="ID_CHALLENGE_PROJECT_TAB_NEW" />
          </div>
          <div 
            className={`challenge__project__tab ${currentTab === "popular" && "tab--active"}`}
            onClick={() => onClickTab("popular")}
          >
            <FormattedMessage id="ID_CHALLENGE_PROJECT_TAB_POPULAR" />
          </div>
        </div>
        <div className="challenge__project__list">
          {
            projects 
            ? projects.length > 0
              ? projects.map(project => (
                <ChallengeProject key={project.id} challengeProject={project} />
                ))
              : <FormattedMessage id="ID_CHALLENGE_PROJECT_SEARCH_EMPTY" />
            : null
          }
        </div>
      </div>
    );
  }
}
