import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { showPopUp } from "../../../../../Common/Component/PopUp";
import SignIn from "../../../../../Common/Component/SignIn";
import SignUp from "../../../../../Common/Component/SignUp";
import closeIcon from "../../../../../Image/challenge-popup-close.svg"
import "./index.scss";

class index extends Component {
  confirmAction = () => {
    showPopUp(
      <SignIn />, 
      { darkmode: true, mobileFullscreen: true }
    )
  }

  cancelAction = () => {
    showPopUp(
      <SignUp />, 
      { darkmode: true, mobileFullscreen: true }
    )
  }

  render() {
    return (
      <div className="challenge-alert-popup challenge-alert-popup--gstar">
        <img 
          onClick={this.props.dismiss}
          className="challenge-alert-popup__close-button" 
          src={closeIcon} 
          alt="close"
        />
        <p className="title">
          <FormattedMessage id="ID_GSTAR_POPUP_WIZLAB_USER_TITLE"/>
        </p>
        <div className="challenge-alert-popup__buttons">
          <button onClick={this.cancelAction}>
            <FormattedMessage id="ID_SIGNIN_SIGNUP"/>
          </button>
          <button onClick={this.confirmAction}>
            <FormattedMessage id="ID_SIGNIN"/>
          </button>
        </div>
      </div>
    )
  }
}

export default index;
