import React, { Component } from "react";
import "./index.scss";

class index extends Component {
  confirmAction = () => {
    // challenge is over click ok
    this.props.dismiss();
    localStorage.setItem("showChallengeOverPopup", "true");
  }

  cancelAction = () => {
    // challenge is over click cancel
    this.props.dismiss();
    localStorage.setItem("showChallengeOverPopup", "false");
  }

  render() {
    return (
      <div className="challenge-end-popup">
        <div className="challenge-end-popup__close-button" onClick={this.props.dismiss} />
        <div className="challenge-end-popup__buttons">
          <button onClick={this.cancelAction} />
          <button onClick={this.confirmAction} />
        </div>
      </div>
    )
  }
}

export default index;
