import React from 'react'
import Slider from "react-slick";
// import TemplateItem
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import DungeonImg from "../../../../Image/thumbnail-Dungeon.png";
import PlaygroundImg from "../../../../Image/thumbnail-playground.png";
import OfficeImg from "../../../../Image/thumbnail-office.png";
import "./index.scss";

class TemplateSlider extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            templateList: [
                DungeonImg,
                PlaygroundImg,
                OfficeImg
            ]
        }

        this.sliderRef = React.createRef();
        this.sliderSetting = {
            arrows: true,
            infiinte: true,
            draggable: false,
            speed: 500,
            slidesToShow: 3,
            slidesToScroll: 3,
            // responsive: [
            // ]
        }

    }

    render() {
        return (
            <>
                <div className="templateItem">
                    <a href="https://studio.redbrick.space/?template=island" target="_blank">
                        <img src={PlaygroundImg} alt='playground-thumbnail' />
                    </a>
                </div>
                <div className="templateItem">
                    <a href="https://studio.redbrick.space/?template=dungeon" target="_blank">
                        <img src={DungeonImg} alt='dungeon-thumbnail' />
                    </a>
                </div>

                <div className="templateItem">
                    <a href="https://studio.redbrick.space/?template=office" target="_blank">
                        <img src={OfficeImg} alt='office-thumbnail' />
                    </a>
                </div>
            </>
        )

    }


}



export default TemplateSlider;
