import React from "react";
import { FormattedMessage, injectIntl } from "react-intl";
import Select from 'react-select';
import PublishedItem from "../../../../Common/Component/PublishedItem";
import searchIcon from "../../../../Image/camp-search.svg";
import viewmoreIcon from "../../../../Image/view-more-arrow.svg";
import "./index.scss";

function View(props) {
  const {
    mode,
    projects,
    currentTab,
    keyword,
    types,
    organizations,
    hasMore,
    onClickTab,
    onChangeKeyword,
    onSubmitKeyword,
    onChangeType,
    onChangeOrganization,
    onClickLoadMore,
    intl
  } = props;

  const customStyles = {
    container: provided => ({
      ...provided,
      minWidth: window.innerWidth > 1064 ? "139px" : "113px",
      height: "48px",
      borderRadius: "4px",
      fontSize: "16px",
      whiteSpace: "nowrap"
    }),
    indicatorSeparator: () => ({
      display: "none"
    }),
    menu: provided => ({
      ...provided,
      padding: "0 14px",
      color: "#888888",
      backgroundColor: "#000417",
      border: "1px solid #3F3F52",
    }),
    option: (provided, state) => ({
      ...provided,
      padding: "4px 0",
      color: state.isSelected ? "#FFFFFF" : "#888888",
      backgroundColor: "transparent",
      cursor: "pointer",
      ":hover": {
        color: "#FFFFFF"
      },
      ":active": {
        backgroundColor: "transparent",
        color: "#FFFFFF"
      }
    }),
    menuList: provided => ({
      ...provided,
      position: "relative",
      maxHeight: "250px",
      overflowY: "scroll"
    }),
    control: provided => ({
      ...provided,
      height: "48px",
      boxShadow: "none",
      backgroundColor: "transparent",
      border: "1px solid #3F3F52",
      padding: 0,
      ":active": { outline: "none" },
      ":focus": { outline: "none" },
      ":hover": {
        cursor: "pointer",
        border: "solid 1px #d8d8d8"
      }
    }),
    valueContainer: provided => ({ 
      ...provided,
      height: "48px"
    }),
    singleValue: (provided) => ({
      ...provided,
      color: "#ffffff"
    }),
    placeholder: provided => ({
      ...provided,
      color: "#888888"
    })
  }

  const isHOF = mode === "hof";
  const tabs = isHOF ?
    [{id: "ssafy6", intlId: "ID_CAMP_HOF_TAB03"}, {id: "ssafy5", intlId: "ID_CAMP_HOF_TAB01"}, {id: "ssafy4", intlId: "ID_CAMP_HOF_TAB02"}] :
    [{id: "new", intlId: "ID_CAMP_PROJECTS_TAB01"}, {id: "like", intlId: "ID_CAMP_PROJECTS_TAB02"}];

  return (
    <div className="camp__projects">
      <div className="camp__projects__filters">
        <div className="camp__projects__tabs">
          {tabs.map(tab=> {
            return (
              <div
                key={tab.id}
                className={`camp__projects__tab ${currentTab === tab.id && "camp__projects__tab--active"}`}
                onClick={()=> onClickTab(tab.id)}
              >
                <FormattedMessage id={tab.intlId} />
              </div>
            );
          })}
        </div>

        <div className={`camp__projects__search--inputs ${isHOF && "camp__projects__search--inputs--hidden"}`}>
          <div className="camp__projects__search--input">
            <input
              type="text"
              value={keyword}
              onChange={onChangeKeyword}
              onKeyUp={e=> e.keyCode === 13 && onSubmitKeyword()}
              placeholder={intl.formatMessage({ id: "ID_CAMP_SEARCH_PLACEHOLDER" })}
            />
            <img src={searchIcon} alt="search" onClick={onSubmitKeyword}/>
          </div>
          <Select
            name="organization"
            styles={customStyles}
            onChange={onChangeOrganization}
            placeholder={intl.formatMessage({ id: "ID_CAMP_SELECT_ORGANIZATION" })}
            options={organizations.map(item=> { return { value: item, label: item }})}
          />
          <Select
            name="type"
            styles={customStyles}
            onChange={onChangeType}
            placeholder={intl.formatMessage({ id: "ID_CAMP_SELECT_TYPE" })}
            options={types.map(item=> { return { value: item, label: item }})}
          />
        </div>
      </div>

      <div className="camp__projects__project--list">
        {projects.map((project, i) => {
          return (
            <PublishedItem key={i} published={project.published} />
          );
        })}
        {projects.length === 0 && <p className="camp__projects__project--list__placeholder">
          <FormattedMessage id="ID_CAMP_LIST_PLACEHOLDER" />
        </p>}
      </div>
      {hasMore && (
        <button className="camp__projects__view-more-button" onClick={onClickLoadMore}>
          <FormattedMessage id="ID_CAMP_PROJECTS_MORE" />
          <img src={viewmoreIcon} alt="view more icon" />
        </button>
      )}
    </div>
  );
}
export default injectIntl(View);
