import React from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import DungeonImg from "../../../../Image/thumbnail-Dungeon.png";
import ObstacleImg from "../../../../Image/thumbnail-obstacle.png";
import DQImg from "../../../../Image/thumbnail-dragonquest.png";
import CaveImg from "../../../../Image/thumbnail-cave.png";
import IslandImg from "../../../../Image/thumbnail-island.png";
import WarehouseImg from "../../../../Image/thumbnail-warehouse.png";
import RedblockImg from "../../../../Image/thumbnail-redblock.png";
import "./index.scss";

class TemplateSlider extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            templateList: [
                DungeonImg
            ]
        }

        this.sliderRef = React.createRef();
        this.sliderSetting = {
            arrows: true,
            infinite: false,
            draggable: false,
            speed: 400,
            slidesToShow: 5.75,
            slidesToScroll: 5,
            // centerMode: true
            // responsive: [
            // ]
        }

    }

    render() {
        return (
            <>
            <div className="template-slider">
                <Slider 
                    {...this.sliderSetting}
                >
                <div className="template-slider-item">
                    <div className="template">
                        <a href="https://studio.redbrick.space/?template=redblock" target="_blank">
                            <img src={RedblockImg} alt='redblock-thumbnail' />
                        </a>
                    </div>
                </div>

                <div className="template-slider-item">
                    <div className="template">
                        <a href="https://studio.redbrick.space/?template=dungeon" target="_blank">
                            <img src={DungeonImg} alt='dungeon-thumbnail' />
                        </a>
                    </div>
                </div>

                <div className="template-slider-item">
                    <div className="template">
                        <a href="https://studio.redbrick.space/?template=island" target="_blank">
                            <img src={IslandImg} alt='island-thumbnail' />
                        </a>
                    </div>
                </div>
                <div className="template-slider-item">
                    <div className="template">
                        <a href="https://studio.redbrick.space/?template=obstacle" target="_blank">
                            <img src={ObstacleImg} alt='obstacle-thumbnail' />
                        </a>
                    </div>
                </div>
                <div className="template-slider-item">
                    <div className="template">
                        <a href="https://studio.redbrick.space/?template=warehouse" target="_blank">
                            <img src={WarehouseImg} alt='warehouse-thumbnail' />
                        </a>
                    </div>
                </div>
                <div className="template-slider-item">
                    <div className="template">
                        <a href="https://studio.redbrick.space/?template=dragonquest" target="_blank">
                            <img src={DQImg} alt='dragonquest-thumbnail' />
                        </a>
                    </div>
                </div>

                <div className="template-slider-item">
                    <div className="template">
                        <a href="https://studio.redbrick.space/?template=cave" target="_blank">
                            <img src={CaveImg} alt='cave-thumbnail' />
                        </a>
                    </div>
                </div>



                </Slider>
            </div>
            </>
        )

    }


}



export default TemplateSlider;
