import moment from "moment";
import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import Validate from "../../../Util/Validate";
import Field from "../../Field";
import "./index.scss";
import * as request from "../../../Util/HTTPRequest";
import PopUp, { showPopUp } from "../"

class EmailAuthPopup extends Component {
  constructor(props) {
    super(props)
    this.state = {
      emailSent: false,
      pendingEmail: ""
    }
  }

  get emailWarningId() {
    return Validate.email(this.state.pendingEmail).id;
  }

  get isOverAge14() {
    const birth = this.props.userInfo.birth;
    const years = moment().diff(birth, "years");
    return years >= 14;
  }

  getVerifyToken() {
    const search = this.props.location.search;
    const param = new URLSearchParams(search);
    const token = param.get("verifyToken");
    return token;
  }

  onSubmit = () => {
    request.sendVerifyEmail({ receiverEmail: this.state.pendingEmail })
      .catch(error => console.error(error));

    this.setState({ emailSent: true }, () => {
      this.props.handlePendingVerify(this.state.pendingEmail);
    });
  }

  showAlert = ({ titleId, subtitleId, buttonNameId }) => {
    const { intl } = this.props;
    showPopUp(
      <PopUp.OneButton
        title={intl.formatMessage({ id: titleId })}
        subtitle={intl.formatMessage({ id: subtitleId })}
        buttonName={intl.formatMessage({ id: buttonNameId })}
      />
    )
  }

  onChange = (id, value) => {
    this.setState({ [id]: value })
  }

  // onClickBack = () => {
  //   this.setState({ emailSent: false })
  // }

  render() {
    const { isOverAge14, emailWarningId, onChange, onSubmit } = this;
    const { pendingEmail, emailSent } = this.state;
    return (
      <div className="email-auth-popup">
        {emailSent
          ? <EmailConfirm
            email={pendingEmail}
            onConfirm={this.props.dismiss}
          />
          : <EmailRegister
            email={pendingEmail}
            isOverAge14={isOverAge14}
            emailWarningId={emailWarningId}
            onChange={onChange}
            onSubmit={onSubmit}
          />}
      </div>
    )
  }
}

export default EmailAuthPopup;

const EmailRegister = props => {
  const { email, isOverAge14, onChange, onSubmit, emailWarningId } = props;
  return (
    <React.Fragment>
      <div className="email-auth-popup__title">
        {isOverAge14
          ? <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_TITLE1" />
          : <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_TITLE2" />
        }
      </div>
      <p className="email-auth-popup__message">
        {isOverAge14
          ? <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_DESC1" />
          : <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_DESC2" />
        }
      </p>
      <Field.Text
        id="pendingEmail"
        value={email}
        onChange={onChange}
        warning={email && emailWarningId && (
          <FormattedMessage id={emailWarningId} />
        )}
        placeholder="Ex) wizlab@wizschool.io"
      />
      <button
        className={`email-auth-popup__button enable--${(email && !emailWarningId) ? true : false}`}
        onClick={onSubmit}
      >
        <FormattedMessage id="ID_EMAIL_VERIFY_REGISTER_BTN" />
      </button>
    </React.Fragment>
  )
}

const EmailConfirm = props => {
  const { email, onConfirm } = props;
  return (
    <React.Fragment>
      <div className="email-auth-popup__title">
        <FormattedMessage id="ID_EMAIL_VERIFY_CONFIRM_TITLE" />
      </div>
      <p className="email-auth-popup__message">
        <FormattedMessage id="ID_MY_ACCOUNT_CONFIRM_POPUP_MSG" values={{ email }} />
      </p>
      <div className="email-auth-popup__confirm">
        <button onClick={onConfirm}>
          <FormattedMessage id="ID_CONFIRM" />
        </button>
      </div>
    </React.Fragment>
  )
}
