import React from "react";
import { injectIntl } from "react-intl";
import "./index.scss";

function index({ intl, TAB_TYPE, selectedTab, onClickTab }) {
  const getTabName = (tab) => {
    switch (tab) {
      case "event":
        return intl.formatMessage({ id: "ID_EVENT_LIVE" });
      case "endedEvent":
        return intl.formatMessage({ id: "ID_EVENT_RESULT" });
      case "notice":
        return intl.formatMessage({ id: "ID_EVENT_NOTICE"})
      default:
        break;
     }
  }

  return (
    <div className="news__tabs">
      {[...Object.keys(TAB_TYPE)].map((tab, index) => {
        return (
          <div
            key={index}
            className={`news__tab 
              ${selectedTab === TAB_TYPE[tab] ? "tab--selected" : ""}`}
            onClick={() => onClickTab(TAB_TYPE[tab])}
          >
            {getTabName(TAB_TYPE[tab])}
          </div>
        );
      })}
    </div>
  )
}

export default injectIntl(index);
