import React from "react";
import ReactQuill from 'react-quill';
import { ImageCompressor } from "../../Util/FileCompressor"
import * as request from "../../Util/HTTPRequest";
import 'react-quill/dist/quill.snow.css';
import "./index.scss";

class QuillEditor extends React.Component {
  constructor(props) {
    super(props);
    this.quillRef = React.createRef();
    this.modules = {
      toolbar: {
        container: [
          [{ header: ["1", "2", "3", false] }],
          ["bold", "italic", "underline", "link"],
          [{ list: "ordered" }, { list: "bullet" }],
          ["image"]
        ],
        handlers: {
          image: this.onClickAddImage
        }
      }
    }
  }

  onClickAddImage = () => {
    const imgInput = this.createImageInput();
    imgInput.onchange = async e => {
      const url = await this.uploadImage(e.currentTarget.files[0]);
      this.insertImage(url);
    };
    imgInput.click();
  }
  createImageInput() {
    const input = document.createElement("input");
    input.setAttribute("type", "file");
    input.setAttribute("accept", ".jpeg, .jpg, .png");
    return input;
  }
  async uploadImage(image) {
    const compressed = await ImageCompressor(image);
    const formData = new FormData();
    formData.append("file", compressed);

    const url = await request.upload(formData)
      .then(res => res.json())
      .then(json => json.url)
      .catch(err => {
        console.error(err);
      });
    return url;
  }
  insertImage(url) {
    const range = this.quillEditor.getSelection(true);
    this.quillEditor.insertEmbed(range.index, "image", url, "user");
    this.quillEditor.setSelection(range.index + 1);
  }
  get quillEditor() {
    return this.quillRef.current.editor;
  }

  render() {
    return (
      <ReactQuill
        ref={this.quillRef}
        modules={this.modules}
        name={this.props.name}
        placeholder={this.props.placeholder}
        value={this.props.value}
        onChange={this.props.onChange}
      />
    );
  }
}

export default QuillEditor;
