import React from "react";
import { Link } from "react-router-dom";
import { FormattedMessage } from "react-intl";
import "./index.scss";

import likeIcon from "../../../../../Image/main-icon-like.svg";
import viewIcon from "../../../../../Image/main-icon-view.svg";
import commentIcon from "../../../../../Image/main-icon-comment.svg";

function View(props) {
  return (
    <div className="challenge__hof">
      <p className="challenge__hof__title">
        <FormattedMessage id="ID_GSTAR_HOF_TITLE03"/>
      </p>
      <div className="challenge__hof__grid">
        {props.hofList
          .filter(hof => hof.type === "season3")
          .map(hof => (
            <div key={hof.pId} className="challenge__hof__grid-item">
              <HOFItem key={hof.pId} hof={hof} />
            </div>
          ))
        }
      </div>
      <p className="challenge__hof__title">
        <FormattedMessage id="ID_CHALLENGE_HOF_TITLE02"/>
      </p>
      <div className="challenge__hof__grid">
        {props.hofList
          .filter(hof => hof.type === "season2")
          .map(hof => (
            <div key={hof.pId} className="challenge__hof__grid-item">
              <HOFItem key={hof.pId} hof={hof} />
            </div>
          ))
        }
      </div>
      <p className="challenge__hof__title">
        <FormattedMessage id="ID_CHALLENGE_HOF_TITLE01"/>
      </p>
      <div className="challenge__hof__grid">
        {props.hofList
          .filter(hof => hof.type === "roomEscape")
          .map(hof => (
            <div key={hof.pId} className="challenge__hof__grid-item">
              <HOFItem key={hof.pId} hof={hof} />
            </div>
          ))
        }
      </div>
    </div>
  );
}

function HOFItem(props) {
  const { hof } = props;
  return (
    <Link
      className="hof"
      to={`/challenge?tab=hallOfFame&pId=${hof.pId}`}
    >
      <div className="hof__icon-wrapper">
        <img className="hof__icon" src={hof.project.icon} alt="icon" />
      </div>
      <div className="hof_section">
        <div className="hof__name">{hof.project.name}</div>
        <div className="hof__user">{hof.project.user.name}</div>
        <div className="hof__counts">
          <div className="hof__count">
            <img src={likeIcon} alt="like" />
            {hof.project.likeCount}
          </div>
          <div className="hof__count">
            <img src={viewIcon} alt="view" />
            {hof.project.viewCount}
          </div>
          <div className="hof__count">
            <img src={commentIcon} alt="comment" />
            {hof.project.commentCount}
          </div>
        </div>
      </div>
    </Link>
  );
}

export default View;
