import React from "react";
import moment from "moment";
import { Link } from "react-router-dom";
import { FormattedMessage } from "react-intl";
import moreIcon from "../../../../Image/icon-news-view-more.svg";
import "./index.scss";

export default function index({ news, onClickMore, newsCount }) {
  return (
    <div className="news__table">
      {news.map((item, index) => {
        const isToday = moment().isSame(item.createdAt, "day");
        return (
          <Link
            to={`news/detail/${item.id}`}
            key={index}
          >
            <div className="news__item pc">
              <div className="news__item__topic">{item.topic}</div>
              <div className="news__item__title">
                {item.title}
                {isToday && <span className="news__item__new-label">
                  <FormattedMessage id="ID_NEWS_NEW_LABEL" />
                </span>}
              </div>
              <div className="news__item__date">
                {moment(item.createdAt).format("YYYY.MM.DD")}
              </div>
            </div>
            <div className="news__item mobile" key={index}>
              <div className="news__item__title">
                {item.title}
                {isToday && <span className="news__item__new-label">
                  <FormattedMessage id="ID_NEWS_NEW_LABEL" />
                </span>}
              </div>
              <div className="news__item__bottom">
                <div className="news__item__topic">{item.topic}</div>
                <div className="news__item__date">
                  {moment(item.createdAt).format("YYYY.MM.DD")}
                </div>
              </div>
            </div>
          </Link>
        );
      })}

      {newsCount > news.length && (
        <div className="news__more-button" onClick={onClickMore}>
          <img src={moreIcon} alt="news more" />
          <FormattedMessage id="ID_NEWS_VIEW_MORE" />
        </div>
      )}
    </div>
  )
}
