import React from "react";
import Tabs from "../CategoryTabs";
import Select from "../CategorySelect";
import "./index.scss";

function Categories(props) {
  return (
    <div className="learn__categories">
      <Tabs 
        tabs={props.categories}
        selectedTabIndex={props.selectedCategoryIndex}
        onClickTabAt={props.onClickCategoryAt}
      />
      <Select
        options={props.categories}
        selectedOptionIndex={props.selectedCategoryIndex}
        onClickOptionAt={props.onClickCategoryAt}
      />
    </div>
  )
}

export default Categories;
