import React from "react";
import "./index.scss"
import CertificateInput from "../../PopUp/CertificateInput";
import { showPopUp } from "../../../../../../Common/Component/PopUp"
import CreatorChallengePopUp from "../../PopUp/CreatorChallenge";
import clearImg from "../../../../../../Image/dream_questClear.svg";
import CertificatePrint from "../../../../../../Common/Component/LearnCertification";
import * as request from "../../../../../../Common/Util/HTTPRequest";

function ProjectClear(props) {

  const OCP2Projects = [1,2,3,4,5,6];

  if(OCP2Projects.includes(props.myProject.projectId)) {
    return  (
      <div className="dream_projectClear">
        <img className="dream_projectClear_image" src={clearImg} alt="clear"/>
        <p className="popup_title">QUEST CLEAR</p>
        <div className="popup_subtitle">
          {`축하합니다! 모든 미션에 성공했어요.\n`}
          {`아이디어를 추가해서 여러분만의 멋진 게임을 만들어 보세요!\n`}
        </div>
        <div className="popup_buttons">
          <button
            className="popup_button popup_button-cancel"
            onClick={() => {
              if (props.onClickCancel) props.onClickCancel();
              if (props.dismiss) props.dismiss();
            }}
          >
            더 해보기
          </button>
          <button
            className="popup_button"
            onClick={alertCertificateInput(props)}
          >
            인증서 출력하기
          </button>
        </div>
      </div>
    );
  } else {
    return (
      <div className="dream_projectClear">
        <img className="dream_projectClear_image" src={clearImg} alt="clear"/>
        <p className="popup_title">QUEST CLEAR</p>
        <div className="popup_subtitle">
          {`축하합니다! 모든 미션에 성공했어요.\n`}
          {`이어서 다양한 자신만의 게임을 만들어보세요!\n`}
        </div>
        <div className="popup_blank"></div>
        <div className="popup_bold">
          {`2021 위즈랩 X G-STAR 챌린저\n`}
        </div>
        <div className="popup_subtitle">
          {`2021.10.15 ~ 2021.11.5 (3주간)\n`}
        </div>
        <div className="popup_blank"></div>
        <div className="popup_subtitle">
          {`국내 최대 규모의 게임 박람회 G-STAR에서\n`}
          {`자신의 게임을 선보일 주인공이 되어보세요!\n`}
        </div>
        <div className="popup_buttons">
          <button
            className="popup_button popup_button-cancel"
            onClick={() => {
              if (props.onClickCancel) props.onClickCancel();
              if (props.dismiss) props.dismiss();
            }}
          >
            나중에 하기
          </button>
          <button
            className="popup_button"
            onClick={goToGstarChanllenges}
          >
            바로가기
          </button>
        </div>
      </div>
    )
  }

}

function alertCertificateInput(props) {
  showPopUp(
    <CertificateInput
      defaultName={props.user.name}
      defaultOrganization={props.user.organization}
      withProjectClear={true}
      onClickConfirm={(name, organization) => {
        alertCertificatePrint(props.user.name, props.user.organization, props.myProject);
      }}
      // onClickCancel={alertChallengeEvent.bind(this)}
    />,
    { dismissButton: false }
  );
}

function alertCertificatePrint(name, organization, myProject) {
  const { project } = myProject;
  showPopUp(
    <CertificatePrint
      title={`${project.category.title} ${project.title}`}
      name={name}
      organization={organization}
      certImageURL={project.certImageURL}
      // callback={alertChallengeEvent.bind(this)}
    />,
    { darkmode: true }
  );
}

function alertChallengeEvent() {
  showPopUp(
    <CreatorChallengePopUp />,
    { defaultPadding: false }
  );
}

function goToGstarChanllenges() {
  const G_StarChallengesLink = window.location.origin + "/challenge";
  window.location.assign(G_StarChallengesLink);
}

export default ProjectClear;
