import React from 'react';
import { injectIntl } from "react-intl";
import Field from "../../Common/Component/Field";
import "./index.scss";

function View(props) {
  const {
    intl,
    allValid,
    onChangeField,
    onClickChange,
    onClickFind,
    password,
    passwordRe,
    isPasswordLengthValid,
    isPasswordCombValid,
    passwordReWarning,
    showWarning,
    failed
  } = props;

  if (failed) return <FailView intl={intl} onClickFind={onClickFind} />

  return (
    <div className="reset-password">
      <div className="reset-password__container">
        <h1>
          {intl.formatMessage({id: "ID_RESET_PW_TITLE"})}
        </h1>
        <div className="reset-password__form">
          <Field.SecureText
            id="password"
            value={password}
            onChange={onChangeField}
            title={intl.formatMessage({id: "ID_MY_ACCOUNT_PW_NEW"})}
            placeholder={intl.formatMessage({id: "ID_SIGNIN_PW_PLACEHOLDER"})}
            secureToggleOption={true}
            informations={[
              {
                text: intl.formatMessage({ id: "ID_SIGNUP_PW_INFO_LENGTH"}),
                valid: password.length ? isPasswordLengthValid : undefined
              },
              {
                text: intl.formatMessage({ id: "ID_SIGNUP_PW_INFO_MIX"}),
                valid: password.length ? isPasswordCombValid : undefined
              }
            ]}
          />
          <Field.SecureText
            id="passwordRe"
            value={passwordRe}
            onChange={onChangeField}
            title={intl.formatMessage({id: "ID_MY_ACCOUNT_PW_RE"})}
            placeholder={intl.formatMessage({id: "ID_SIGNIN_PW_PLACEHOLDER"})}
            secureToggleOption={true}
            warning={showWarning ? passwordReWarning : undefined}
          />
        </div>
        <button className={`enable--${allValid}`} onClick={onClickChange}>
          {intl.formatMessage({id: "ID_RESET_PW_BUTTON"})}
        </button>
      </div>
    </div>
  );
}

function FailView({ intl, onClickFind }) {
  return (
    <div className="reset-password">
      <h1>
        {intl.formatMessage({id: "ID_RESET_PW_FAIL_TITLE"})}
      </h1>
      <p className="reset-password__desc">
        {intl.formatMessage({id: "ID_RESET_PW_FAIL_DESC"})}
      </p>
      <button onClick={onClickFind}>
        {intl.formatMessage({id: "ID_FORGOTPW"})}
      </button>
    </div>
  );
}

export default injectIntl(View);
