import React from "react";
import { FormattedMessage } from "react-intl"
import CreatorRank from "../CreatorRank";
import "./index.scss";

function View(props) {
  const tabs = [
    "likeCount",
    "subscribeCount",
    "levelPoint"
  ];
  const tabFormatId = {
    likeCount: "like",
    subscribeCount: "subscribe",
    levelPoint: "levelPoint"
  }
  return (
    <div className="creator-ranks">
      <div className="creator-ranks__tabs">
        {tabs.map(tab => (
          <button
            key={tab}
            className={
              "creator-ranks__tab"
              + (tab === props.selectedTab ? " creator-ranks__tab--selected" : "")
            }
            onClick={() => {
              props.onClickTab(tab);
            }}
          >
            <FormattedMessage id={"ID_HOME_RANK_CREATOR_" + tabFormatId[tab].toUpperCase()}/>
          </button>
        ))}
      </div>
      <div className="creator-ranks__items">
        {props.creators.map((creator, index) => (
          <div key={creator.id} className="creator-ranks__item">
            <CreatorRank
              creator={creator}
              rank={index + 1}
              tab={props.selectedTab}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default View;
