import React, { Fragment } from 'react';
import { injectIntl } from "react-intl";
import { Link } from "react-router-dom";
import DropDown from "../DropDown";
import Notification from "../Notification";
import { Fade } from "@material-ui/core";
import logoImg from "../../../../Image/redbrick_logo.svg";
import NotiIcon from "../../../../Image/icon_notice.svg";
import mobileMenuOn from "../../../../Image/combined-shape.svg";
import mobileMenuOff from "../../../../Image/group-2.svg";

function View(props) {
  return (
    <div className={`header ${props.scrollTop ? "scroll_top" : ""}`}>
      <PcHeader {...props} />
      <MobileHeader {...props} />
    </div>
  );
}

function PcHeader(props) {
  const { userinfo } = props;
  return (
    <div className="header_pc">
      <div className="header_logo">
        <Link
          to="/"
          onClick={() => {
            window.scrollTo(0, 0);
          }}
        >
          <img src={logoImg} alt="로고 이미지" />
        </Link>
      </div>
      <Links {...props} />

      <div className="header_menus">
        {userinfo.name ? (
          <PcLoginMenu {...props} />
        ) : (
          <PcLogoutMenu {...props} />
        )}
      </div>
    </div>
  );
}

function MobileHeader(props) {
  const {
    badge,
    isNotiPopupOn,
    onClickNotification,
    handleToggleMobileMenu,
    userinfo,
    isMobileMenuOn
  } = props;

  return (
    <div className="header_mobile">
      <div className="header_logo">
        <Link
          to="/"
          onClick={() => {
            window.scrollTo(0, 0);
          }}
        >
          <img src={logoImg} alt="로고 이미지" />
        </Link>
      </div>
      <div className="header_menu_right">
        {!isMobileMenuOn &&
          <div className="header_lang">
            <DropDown type="lang" />
          </div>
        }
        {!isMobileMenuOn && userinfo.name && (
          <div className="header_menu_noti-wrap">
            <img
              className="header_menus-noti"
              src={NotiIcon}
              alt="img"
              onClick={onClickNotification}
            />
            {badge > 0 && <div className="header_menus-noti-on" />}
            {isNotiPopupOn && (
              <Fade timeout={500} in={isNotiPopupOn}>
                <div className="header_notification">
                  <Notification
                    onClickNotification={onClickNotification}
                    userinfo={userinfo}
                  />
                </div>
              </Fade>
            )}
          </div>
        )}
        <div className="header_menu_icon">
          <img
            onClick={handleToggleMobileMenu}
            src={isMobileMenuOn ? mobileMenuOff : mobileMenuOn}
            alt="menu"
          />

          {/* todo: 챌린지 종료 후 삭제 */}
          {/* {!isMobileMenuOn && <div className="header_menus-noti-on" />} */}
        </div>
      </div>
      <MobileMenu {...props} />
    </div>
  );
}

function PcLoginMenu(props) {
  const {
    badge,
    isNotiPopupOn,
    onClickNotification,
    userinfo
  } = props;
  return (
    <>
    <DropDown type="user" user={userinfo} />
    <div className="header_menus-noti-wrap">
      <img
        className="header_menus-noti"
        src={NotiIcon}
        alt="img"
        onClick={onClickNotification}
      />
      {badge > 0 && <div className="header_menus-noti-on" />}
    </div>
    {isNotiPopupOn && (
      <Fade timeout={500} in={isNotiPopupOn}>
        <div className="header_notification">
          <Notification
            onClickNotification={onClickNotification}
            userinfo={userinfo}
          />
        </div>
      </Fade>
    )}

    <div className="header_menus-login-lang">
      <DropDown type="lang" />
    </div>

    <PcCreateBtn {...props} />
    </>
  );
}
function PcLogoutMenu(props) {
  const {
    intl,
    onClickSignIn,
    onClickLogout
  } = props;
  return (
    <Fragment>
      <PcCreateBtn {...props} />
      <button
        className="header_menu--btn sign-in-btn"
        onClick={onClickSignIn}
      >
        {intl.formatMessage({ id: `ID_HEADER_LOGIN` })}
      </button>
      {/* <div
        className="header_menu"
        id="header_menu_signUp"
        onClick={onClickSignUp}
      >
        {intl.formatMessage({ id: `ID_HEADER_SIGNUP` })}
      </div> */}
      <DropDown type="lang" />
    </Fragment>
  );
}

function MobileMenu(props) {
  const {
    intl,
    userinfo,
    isMobileMenuOn,
    onClickLogout
  } = props;

  return (
    <div
      className={`header_menu_content header_menu_content${
        isMobileMenuOn ? "--on" : "--off"
      }`}
    >
      {userinfo.name ? (
        <MobileLoginMenuHeader {...props} />
      ) : (
        <MobileLogoutMenuHeader {...props} />
      )}
      <Links {...props} />
      <div
        className="header-menu logout"
        onClick={onClickLogout}
      >
        {intl.formatMessage({
          id: `ID_HEADER_LOGOUT`
        })}
      </div>
    </div>
  );
}

function MobileLoginMenuHeader(props) {
  const {
    intl,
    // onClickLogout,
    handleClickURL,
    userinfo
  } = props;
  return (
    <Fragment>
      <div
        className="header-menu mypage"
        onClick={() => handleClickURL("/mypage")}
      >
        <img
          src={userinfo.icon}
          alt="usericon"
          className="usericon"
        />
        <div
          className="userinfo_wrapper"
        >
          <p className="username_text">{userinfo.name}</p>
          <p className="mypage_text">
            {intl.formatMessage({ id: `ID_HEADER_MYPAGE` })}
          </p>
        </div>
      </div>
      <div className="header-menu my">
        <div
          className="header-menu__my-button"
          onClick={() => handleClickURL("/myapk")}
        >
          {intl.formatMessage({ id: `ID_USERDD_MYAPK` })}
        </div>
        <div
          className="header-menu__my-button"
          onClick={() => handleClickURL("/myaccount")}
        >
          {intl.formatMessage({ id: `ID_USERDD_ACCOUNT` })}
        </div>
      </div>
    </Fragment>
  );
}
function MobileLogoutMenuHeader(props) {
  const {
    intl,
    onClickSignIn,
    // onClickSignUp
  } = props;
  return (
    <div className="header_button_wrapper">
      <button
        className="header_menu header_menu--btn sign-in-btn"
        onClick={onClickSignIn}
      >
        {intl.formatMessage({ id: `ID_HEADER_LOGIN` })}
      </button>
    </div>
  );
}

function PcCreateBtn(props) {
  const {
    intl,
    isMobileMenuOn
  } = props;
  return (
    <Link 
      to="/builder"
        onClick={() =>{
          window.scrollTo(0,0);
        }}
    >
      <button className="header_menu--btn create-btn">
        {intl.formatMessage({id: `ID_HEADER_LINK_BUILDER`})} 
      </button>
    </Link>
  );
}

function Links(props) {
  const {
    intl,
    currentLink,
    links,
    highlightLinks,
    isMobileMenuOn
  } = props;
  return (
    <div className="header_links">
      {links
        .filter(link => isMobileMenuOn ? link !== "myapk" && link !== "builder" : link !== "myapk")
        .map((link, index) => {
          const isBuilderLink = link === "builder";
          return (
            <Link
              key={index}
              to={link === "social" ? "/social/recent" : `/${link}`}
              target={isBuilderLink ? "_blank" : undefined}
              rel={isBuilderLink ? "noopener noreferrer" : undefined}
              className={`header_link ${currentLink === link && "header_link_on"} header_link_${link} ${highlightLinks.includes(link) && "header_link_highlight"}`}
              onClick={() => link && window.scrollTo(0, 0)}
            >
              {intl.formatMessage({
                id: `ID_HEADER_LINK_${link.toUpperCase()}`
              })}
            </Link>
          );
        })}
    </div>
  );
}

export default injectIntl(View);
