import React from "react";
import styled from "@emotion/styled";
import Slider from "react-slick";
import Container from "./Container";
import ContentItem from "./ContentItem";
import Title from "./TitleLabel";
import SubTitle from "./SubTitleLabel";
import logo from "../../../Image/dreamclass/logo.svg";

const Self = styled.div`
  width: 100%;
  max-width: 1175px;
  margin: 0 auto;
  min-height: 510px;
  @media screen and (max-width: 1169px) {
    min-height: unset;
  }
`;

const Logo = styled.img`
  position: absolute;
  top: 51px;
  right: 0;

  @media screen and (max-width: 1169px) {
    position: relative;
    top: unset;
    margin-left: calc((100vw - 88.33vw) / 2);
    margin-bottom: 50px;
  }
`;

const ContentsWarp = styled.div`
  margin: 30px -15px 0;

  @media screen and (max-width: 1169px) {
    margin-top: 50px;

    .slick-slider {
      margin-left: calc((154 / 360) * 100vw * -1);
    }
  }
`;

const Contents = ({ children, items, ...props }) => {
  const sliderSettings = {
    slidesToShow: 4,
    slidesToScroll: 1,
    arrows: true,
    dots: false,
    infinite: items.length > 4 ? true : false,
    autoplay: true,
    responsive: [
      {
        breakpoint: 1169,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          arrows: false,
          dots: false,
          infinite: true,
          autoplay: true,
          centerMode: true,
          variableWidth: true, // mobile slide 최소 너비 설정
          adaptiveHeight: true // 높이 값에 여백 생김 제거
        }
      }
    ]
  };

  return (
    <Self {...props}>
      <Container>
        <Logo src={logo} alt="logo" />
        <Title>시작부터 완성까지,</Title>
        <SubTitle>프로그래밍 개념을 익히면서 동시에 앱을 창작해 보세요.</SubTitle>
        <ContentsWarp>
          <Slider className="slick-contents" {...sliderSettings}>
            {items.map((item) => (
              <ContentItem key={item.id} item={item} style={{ width: "calc((154 / 360) * 100vw)" }}/>
            ))}
          </Slider>
        </ContentsWarp>
      </Container>
    </Self>
  );
};

export default Contents;
