import React from "react";
import { withRouter } from "react-router-dom";
import { isIE } from "react-device-detect";
import { showPopUp } from "../Component/PopUp";
import AlertIEPopup from "../Component/AlertIEPopup";

function CheckIE(props) {
  const { location } = props;
  if ( location.pathname === "/" || location.pathname.startsWith("/game") || location.pathname.startsWith("/learn") ) {
    const didShowIEPopup = localStorage.getItem("didShowIEPopup");
    if ( isIE && !didShowIEPopup ) {
      localStorage.setItem("didShowIEPopup", true);
      showPopUp(<AlertIEPopup />, {
        darkmode: true,
        mobileFullscreen: true,
        defaultPadding: false
      });
    }
  }
  return null;
}

export default withRouter(CheckIE);
