import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { injectIntl } from "react-intl";
import * as request from "../../Common/Util/HTTPRequest";
import PopUp, { showPopUp } from "../../Common/Component/PopUp";
import View from "./View";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = { challengeItem: undefined };
  }

  componentDidMount() {
    this.getData();
  }

  componentDidUpdate(prevProps) {
    if(prevProps.match.params.id !== this.props.match.params.id) {
      this.getData();
    }
  }

  getData = () => {
    request.getCreatorChallenge({id: this.props.match.params.id, isReview : this.props.match.params.type ? "true" : "false" })
      .then(res=> res.json())
      .then(json=> this.setState({ challengeItem: json}))
      .catch(e=> console.error(e))
  }

  getDataByType = type => {
    const params = { type, id: this.props.match.params.id, isReview : this.props.match.params.type ? "true" : "false" };
    request.getCreatorChallengeByType(params)
      .then(res=> res.json())
      .then(json=> {
        if(json) {
          this.props.history.push(`/challenge2021/project/${json.id}`);
        } else {
          showPopUp(
            <PopUp.OneButton
              title={this.props.intl.formatMessage({ id: "ID_CHALLENGE_DETAIL_PROJECT_EMPTY" })}
              buttonName={this.props.intl.formatMessage({ id: "ID_CHALLENGE_DETAIL_PROJECT_OK"})}
            />,
            {
              darkmode: true,
              dismissButton: false,
              dismissOverlay: true
            }
          );
        }
      })
      .catch(e=> console.error(e))
  }

  onClickPrev = () => {
    this.getDataByType("prev");
  }

  onClickNext = () => {
    this.getDataByType("next");
  }

  onClickList = () => {
    // temp challenge review
    if(!this.props.match.params.type) {
      this.props.history.push("/challenge2021?tab=project");
    } else {
      this.props.history.push("/challengeReview2021");
    }
  }

  onClickPlay = () => {
    const { challengeItem } = this.state;
    this.props.history.replace(`?pId=${challengeItem.project.pId}&isReview=${this.props.match.params.type ? "true" : "false"}`);
  }

  render() {
    const { challengeItem } = this.state;
    const {
      onClickPrev,
      onClickNext,
      onClickList,
      onClickPlay
    } = this;
    return (
      <View
        challengeItem={challengeItem}
        onClickPrev={onClickPrev}
        onClickNext={onClickNext}
        onClickList={onClickList}
        onClickPlay={onClickPlay}
      />
    );
  }
}

export default withRouter(injectIntl(Container));
