import React from "react";
import "./index.scss";

import oobcDefaultIcon from "../../../../Image/learn-tab-oobc-default-icon.png";
import oobcActiveIcon from "../../../../Image/learn-tab-oobc-active-icon.png";
import jsDefaultIcon from "../../../../Image/learn-tab-js-default-icon.png";
import jsActiveIcon from "../../../../Image/learn-tab-js-active-icon.png";
import arrowIcon from "../../../../Image/option-arrow.svg";

class Select extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      folded: true
    }
  }

  get selectedOption() {
    return this.props.options[this.props.selectedOptionIndex];
  }
  onClickSelected = () => {
    this.setState(prevState => ({
      folded: !prevState.folded
    }))
  }
  onClickOptionAt = index => {
    this.setState({ folded: true });
    this.props.onClickOptionAt(index);
  }

  render() {
    if(this.props.options.length <= 0) {
      return null;
    }

    return (
      <div className="learn__select">
        <div
          className="learn__option learn__option--selected"
          onClick={this.onClickSelected}
        >
          <img
            className="learn__option__icon"
            src={ this.selectedOption.subtitle === "텍스트코딩" ? jsActiveIcon : oobcActiveIcon }
            alt="option-icon"
          />
          <div className="learn__option__title">{this.selectedOption.title}</div>
          <img
            className={`learn__option__arrow ${this.state.folded && "arrow--folded"}`}
            src={arrowIcon}
            alt="option-arrow"
          />
        </div>
        {this.state.folded || (
          <div className="learn__select__options">
            {this.props.options.map((option, index) => {
              const selected = index === this.props.selectedOptionIndex;
              if(selected) {
                return null;
              } else {
                return (
                  <div
                    key={index}
                    className="learn__option"
                    onClick={() => {
                      this.onClickOptionAt(index);
                    }}
                  >
                    <img
                      className="learn__option__icon"
                      src={ option.subtitle === "텍스트코딩" ? jsDefaultIcon : oobcDefaultIcon}
                      alt="option-icon"
                    />
                    <div className="learn__option__title">{option.title}</div>
                  </div>
                )
              }
            })}
         </div>
        )}
      </div>
    )
  }
}

export default Select;
