import React from "react";
import { FormattedMessage } from "react-intl"
import PublishedItem from "../../../../Common/Component/PublishedItem";
import "./index.scss";

function View(props) {
  const tabs = ["like", "view", "comment", "news"];
  return (
    <div className="weekly-bests">
      <div className="weekly-bests__tabs">
        {tabs.map(tab => (
          <button
            key={tab}
            name={tab}
            className={
              "weekly-bests__tab"
              + (tab === props.selectedTab ? " weekly-bests__tab--selected" : "")
            }
            onClick={() => {
              props.onClickTab(tab);
            }}
          >
            <FormattedMessage id={"ID_HOME_WEEKLY_BEST_" + tab.toUpperCase()}/>
          </button>
        ))}
      </div>
      <div className="weekly-bests__items">
        {props.weeklyBests.map(weeklyBest => (
          <div key={weeklyBest.id || weeklyBest.pId} className="weekly-bests__item">
            <PublishedItem
              published={
                weeklyBest.published
                || weeklyBest.project
                || weeklyBest}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default View;
