import React, { Component } from "react";
import { connect } from "react-redux";
import { injectIntl } from "react-intl";
import { withRouter } from "react-router-dom";
import * as request from "../../Common/Util/HTTPRequest";
import * as userInfoActions from "../../Common/Store/Reducer/UserInfo";
import PopUp, { showPopUp } from "../../Common/Component/PopUp";
import AccountUpdatePopup from "../../Common/Component/AccountUpdatePopup";
import EmailVerifiedPopup from "../../Common/Component/EmailVerifiedPopup";
import View from "./View";

class Container extends Component {

  componentDidMount() {
    if (this.emailVerifyToken) {
      this.verifyEmail(this.emailVerifyToken);
    } else {
      this.informAccountUpdateIfNeeded();
    }
  }

  componentDidUpdate(prevProps) {
    if (prevProps.user.email !== this.props.user.email) {
      this.informAccountUpdateIfNeeded();
    }
  }

  get emailVerifyToken() {
    const search = this.props.location.search;
    const param = new URLSearchParams(search);
    const token = param.get("verifyToken");
    return token;
  }

  verifyEmail(token) {
    request.updateVerifiedEmail({ token })
      .then(res => res.json())
      .then(result => {
        if (result.success) {
          this.handleEmailVerifySuccess(result);
        } else {
          this.handleEmailVerifyExpired();
        }
      })
      .catch(error => console.error(error))
  }
  handleEmailVerifySuccess = result => {
    if (this.props.user.email === result.email) {
      this.props.updateUserInfo({ verifiedEmail: result.verifiedEmail })
    }
    showPopUp(
      <EmailVerifiedPopup
        isAgeOver14={result.isOver14Years}
        userName={result.name}
      />,
      { darkmode: true }
    );
  }
  handleEmailVerifyExpired = () => {
    showPopUp(
      <PopUp.OneButton
        title={this.props.intl.formatMessage({
          id: "ID_EMAIL_VERIFY_EXPIRED"
        })}
        buttonName={this.props.intl.formatMessage({
          id: "ID_POPUP_WARNING_REPEAT_CLICK_BTN"
        })}
      />,
      { darkmode: true }
    );
  }

  informAccountUpdateIfNeeded() {
    const { email, verifiedEmail } = this.props.user;
    const isOldAccountUser = email && email.includes('@');
    if (isOldAccountUser && !verifiedEmail) {
      showPopUp(<AccountUpdatePopup history={this.props.history} />, { darkmode: true })
    }
  }

  render() {
    return (
      <View />
    );
  }
}

export default connect(
  state => ({
    user: state.userinfo
  }),
  {
    updateUserInfo: userInfoActions.updateUserInfo
  }
)(injectIntl(withRouter(Container)));
