import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { injectIntl } from "react-intl"
import { connect } from "react-redux";
import moment from "moment";
import * as request from "../../Common/Util/HTTPRequest";
import PopUp, { showPopUp } from "../../Common/Component/PopUp";
import Terms from "./Terms";
import View from "./View";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isCompleted: false,
      publishedList: [],
      publishedTotal: 0,
      selectedPublishedIndex: 0,
      productTitle: "",
      productDescription: "",
      teamName: "",
      teamDescription: "",
      teamMemberIds: [""],
      applicantName: "",
      applicantOrganization: "",
      applicantEmail: "",
      applicantPhone: "",
      applicantBirth: moment().subtract(14, 'year'),
      parentName: "",
      parentPhone: "",
      agentName: "",
      agentPhone: "",
      agentRelation: "",
      agreesToEvent: true,
      agreesToApply: false
    }
    this.publishedListRef = React.createRef();
  }
  async componentDidMount() {
    // challenge is over
    this.props.history.push(`/challenge2021/projectlist`);
    return;

    if(this.props.user.id) {
      if(this.isEditMode) {
        await this.loadAppliance(this.applianceId);
      }
      this.loadPublishedList({ offset: 0, limit: 10 });
    }
    if(this.publishedListRef.current) {
      this.publishedListRef.current.addEventListener("scroll", this.onScrollPublishedList);
    }
  }
  componentWillUnmount() {
    if(this.publishedListRef.current) {
      this.publishedListRef.current.removeEventListener("scroll", this.onScrollPublishedList);
    }
  }
  loadPublishedList(options) {
    this.isLoadingPublishedList = true;
    request
      .getPublishedProjects({
        email: this.props.user.email,
        offset: options.offset,
        limit: options.limit
      })
      .then(res => res.json())
      .then(result => {
        this.setState(prevState => ({
          publishedList: [].concat(
            prevState.publishedList,
            result.rows.filter(
              published => this.applianceOrigin 
                ? published.pId !== this.applianceOrigin.project.pId
                : true
            )
          ),
          publishedTotal: result.count
        }));
      })
      .finally(() => {
        this.isLoadingPublishedList = false;
      })
  }
  async loadAppliance(id) {
    await request
      .loadCreatorChallenge(id)
      .then(res => res.json())
      .then(challenge => {
        this.applianceOrigin = challenge;

        this.setState({
          publishedList: [challenge.project],
          productTitle: challenge.productTitle,
          productDescription: challenge.productDescription,
          teamName: challenge.teamName,
          teamDescription: challenge.teamDescription,
          teamMemberIds: JSON.parse(challenge.teamMemberIds),
          applicantName: challenge.applicantName,
          applicantOrganization: challenge.applicantOrganization,
          applicantEmail: challenge.applicantEmail,
          applicantPhone: challenge.applicantPhone,
          applicantBirth: moment(challenge.applicantBirth),
          parentName: challenge.parentName,
          parentPhone: challenge.parentPhone,
          agentName: challenge.agentName,
          agentPhone: challenge.agentPhone,
          agentRelation: challenge.agentRelation,
          agreesToEvent: challenge.isEventAgreed,
          agreesToApply: true
        });
      });
  }

  get applianceId() {
    return this.props.match.params.id;
  }
  get isEditMode() {
    return !!this.applianceId;
  }
  get isAgentRequired() {
    const applicantAge = moment().diff(this.state.applicantBirth, "Y");
    return applicantAge < 14;
  }

  onScrollPublishedList = e => {
    if(
      e.target.scrollTop >= e.target.scrollHeight - e.target.clientHeight - 60
      && this.state.publishedList.length < this.state.publishedTotal
      && !this.isLoadingPublishedList
    ) {
      this.loadPublishedList({ offset: this.state.publishedList.length, limit: 10 });
    }
  }
  onClickPublishedAt = index => {
    this.setState({ selectedPublishedIndex: index });
  }
  onChangeField = (name, value) => {
    this.setState({ [name]: value });
  }
  onClickAddTeamMember = () => {
    const teamMemberIds = this.state.teamMemberIds;
    if(teamMemberIds.length < 3) {
      this.setState({
        teamMemberIds: [ ...teamMemberIds, "" ]
      });
    }
  }
  onChangeTeamMemberIdAt = (index, value) => {
    const teamMemberIds = [ ...this.state.teamMemberIds ];
    teamMemberIds[index] = value;
    this.setState({ teamMemberIds });
  }
  onClickTermsLink = e => {
    e.preventDefault();
    showPopUp(
      <Terms
        onClickConfirm={() => {
          this.setState({ agreesToApply: true });
        }}
      />
    );
  }
  onClickSubmit = () => {
    if(this.isSubmitEnabled) {
      if(this.isEditMode) {
        this.updateAppliance(this.applianceId);
      } else {
        this.submitAppliance();
      }
    }
  }
  onClickComplete = () => {
    this.props.history.push("/challenge2021/projectlist");
  }

  updateAppliance(id) {
    const changed = this.getChanged(this.applianceOrigin ,this.appliance);
    request
      .updateCreatorChallenge(id, changed)
      .then(res => res.json())
      .then(result => {
        if(result.success) {
          this.setState({ isCompleted: true });
        } else {
          throw new Error("failed response");
        }
      })
      .catch(err => {
        console.error(err);
        this.showError();
      })
  }
  submitAppliance() {
    request
      .addCreatorChallenge(this.appliance)
      .then(res => res.json())
      .then(challenge => {
        if(challenge.id) {
          this.setState({ isCompleted: true });
        } else {
          throw new Error("failed response");
        }
      })
      .catch(err => {
        console.error(err);
        this.showError();
      })
  }

  get isSubmitEnabled() {
    return (
      this.state.agreesToApply
      && this.getInvalidFields(this.appliance).length <= 0
    );
  }
  getInvalidFields(appliance) {
    const fields = [];
    for(let field of this.requiredFields){
      const value = appliance[field];
      if(!value) {
        fields.push(field);
      }
    }
    return fields;
  }
  get requiredFields() {
    const fields = [
      "productTitle",
      "productDescription",
      "teamName",
      "teamDescription",
      "applicantName",
      "applicantOrganization",
      "applicantEmail",
      "applicantPhone",
      "applicantBirth",
      "parentName",
      "parentPhone",
      "userId",
      "pId"
    ];

    if(this.isAgentRequired) {
      fields.push(
        "agentName",
        "agentPhone",
        "agentRelation",
      )
    }

    return fields;
  }
  get appliance() {
    return {
      productTitle: this.state.productTitle,
      productDescription: this.state.productDescription,
      teamName: this.state.teamName,
      teamDescription: this.state.teamDescription,
      teamMemberIds: this.stringifiedTeamMemberIds,
      applicantName: this.state.applicantName,
      applicantOrganization: this.state.applicantOrganization,
      applicantEmail: this.state.applicantEmail,
      applicantPhone: this.state.applicantPhone,
      applicantBirth: this.state.applicantBirth.format("YYYY-MM-DD"),
      parentName: this.state.parentName,
      parentPhone: this.state.parentPhone,
      agentName: this.state.agentName,
      agentPhone: this.state.agentPhone,
      agentRelation: this.state.agentRelation,
      isEventAgreed: this.state.agreesToEvent,
      userId: this.props.user.id,
      pId: this.selectedPublished ? this.selectedPublished.pId : null
    }
  }
  get stringifiedTeamMemberIds() {
    const filtered = this.state.teamMemberIds.filter(teamMemberId => !!teamMemberId);
    return JSON.stringify(filtered);
  }
  get selectedPublished() {
    return this.state.publishedList[this.state.selectedPublishedIndex];
  }

  getChanged(from, to) {
    let changed = {};
    for(let key in to) {
      if(to[key] !== from[key]) {
        changed[key] = to[key];
      }
    }
    return changed;
  }

  showError() {
    showPopUp(
      <PopUp.OneButton
        title={this.props.intl.formatMessage({ id: "ID_CHALLENGE_APPLY_SERVER_ERROR_TITLE" })}
        subtitle={this.props.intl.formatMessage({ id: "ID_CHALLENGE_APPLY_SERVER_ERROR_SUBTITLE" })}
        buttonName={this.props.intl.formatMessage({ id: "ID_CONFIRM" })}
      />
    )
  }

  render() {
    return (
      <View
        {...this.state}
        publishedListRef={this.publishedListRef}
        isEditMode={this.isEditMode}
        isAgentRequired={this.isAgentRequired}
        isSubmitEnabled={this.isSubmitEnabled}
        onClickPublishedAt={this.onClickPublishedAt}
        onChangeField={this.onChangeField}
        onClickAddTeamMember={this.onClickAddTeamMember}
        onChangeTeamMemberIdAt={this.onChangeTeamMemberIdAt}
        onClickTermsLink={this.onClickTermsLink}
        onClickSubmit={this.onClickSubmit}
        onClickComplete={this.onClickComplete}
      />
    );
  }
}
export default connect(
  state => ({
    user: state.userinfo
  })
)(withRouter(injectIntl(Container)));
