import React from "react";
import PublishedItem from "../../../../Common/Component/PublishedItem/index";
import PlusIcon from "../../../../Image/more-bttn-plus.svg";
import "./index.scss";

function View(props) {
  return (
    <div className="weeklyTops">
      <div className="weeklyTops__grid">
        {props.weeklyTops.map(weeklyTop => (
          <div key={weeklyTop.id} className="weeklyTop">
            <PublishedItem published={weeklyTop.published || weeklyTop.project} />
          </div>
        ))}
      </div>
      {props.showsMore && (
        <button
          className="weeklyTops__more-btn"
          onClick={props.onClickMore}
        >
          <img className="weeklyTops__more-btn_plus" src={PlusIcon} alt="plus-icon"/>
          더보기
        </button>
      )}
    </div>
  );
}

export default View;
