import React from "react";
import { withRouter } from "react-router-dom";
// import FAQChatbot from "./FAQChatbot";

class RouteListener extends React.Component {

  componentDidMount() {
    // if(!this.isLocatedAtBuilder) {
    //   FAQChatbot.boot();
    // }
  }

  get isLocatedAtBuilder() {
    const builderPaths = [
      "builder",
      "builder_edit",
      "builder_readonly",
      "qna_readonly",
      "wizlive",
      "wizlive_1v4",
      "monitor",
      "monitor_1v4",
      "videoclass",
      "dreamclass",
      "ocp",
      "ocp2",
      "ocp2js",
      "sef",
      "tutorial",
      "recordPlayer",
    ];

    const matches = this.props.location.pathname.match(/[^/]+/g)
    const currentPath = matches ? matches[0] : "";

    return builderPaths.includes(currentPath);
  }

  render() {
    return null;
  }
}

export default withRouter(RouteListener);
