import React from "react";
import Header from "../../../../Common/Component/Layout/Header";
import Main from "./Component/Main";
import MakingNew from "./Component/MakingNew";
import MyProject from "./Component/MyProject";
import MyPublished from "./Component/MyPublished";
import MyApk from "./Component/MyApk";
import News from "./Component/News";
import arrowIcon from "../../../../Image/option-arrow.svg";
import "./index.scss";

export default function View(props) {
  const {
    pageList,
    onClickPage,
    currentPageId,
    setCurrentPage,
    handleEdit,
    onClickProjectEdit,
    onClickDetailBtn,
    selectProject,
    resetSelectProject,
    handleDelete,
    handleCopy,
    isClickedPageList,
    onClickPageList
  } = props;

  let content;
  switch (currentPageId) {
    case "makingNew":
      content = <MakingNew setCurrentPage={setCurrentPage} />;
      break;
    case "myProject":
      content = (
        <MyProject
          setCurrentPage={setCurrentPage}
          handleEdit={handleEdit}
          handleDelete={handleDelete}
          handleCopy={handleCopy}
          onClickDetailBtn={onClickDetailBtn}
          selectProject={selectProject}
        />
      );
      break;
    case "myPublished":
      content = (
        <MyPublished
          setCurrentPage={setCurrentPage}
          onClickProjectEdit={onClickProjectEdit}
          onClickDetailBtn={onClickDetailBtn}
          selectProject={selectProject}
        />
      );
      break;
    case "myApk":
      content = <MyApk setCurrentPage={setCurrentPage} />;
      break;
    case "main":
    default:
      content = (
        <Main
          resetSelectProject={resetSelectProject}
          setCurrentPage={setCurrentPage}
          handleEdit={handleEdit}
          handleDelete={handleDelete}
          handleCopy={handleCopy}
          onClickProjectEdit={onClickProjectEdit}
          onClickDetailBtn={onClickDetailBtn}
          selectProject={selectProject}
        />
      );
      break;
  }
  return (
    <div className="builder--home">
      <Header />
      <div className="builder--home__inner">
        <div className="home--content">
          <div className="content--left">
            <div className="pageList pc">
              {pageList &&
                pageList.map((item, index) => {
                  const { id, title, onImg, offImg } = item;
                  return (
                    <div
                      className={`pageList__item ${
                        currentPageId === id ? "pageList__item__active" : ""
                      }`}
                      key={index}
                      onClick={() => onClickPage(id)}
                    >
                      <img src={currentPageId === id ? onImg : offImg} alt="" />
                      <div>{title}</div>
                    </div>
                  );
                })}
            </div>
            {pageList && pageList.length === 1 ? 
              null :
              <div className="pageList mobile">
                <div className="pageList__mobile__current-tab">
                  {pageList.map((item, index) => (
                    currentPageId === item.id &&
                      <div
                        key={index}
                        className="pageList__item__current-tab__box pageList__item__active"
                        onClick={onClickPageList}
                      >
                        <img src={item.onImg} alt="" />{item.title}
                        <img className={`arrow-icon ${isClickedPageList ? "rotate" : ""}`} src={arrowIcon} alt="" />
                      </div>
                  ))}
                </div>
                <div className="pageLists">
                  {isClickedPageList && pageList.map((item, index) => (
                    currentPageId !== item.id && 
                      <div
                        key={index}
                        className="pageList__item"
                        onClick={() => onClickPage(item.id)}
                      >
                        <img src={item.offImg} alt="" />
                        <div>{item.title}</div>
                      </div>
                  ))}
                </div>
              </div>
            }
            <News />
          </div>
          <div className="content--right">{content}</div>
        </div>
      </div>
    </div>
  );
}
