import React from "react";
import { detectIE } from "../../Util/detectBrowser";
import Header from "./Header";
import Body from "./Body";
import Footer from "./Footer";
import IEPopup from "../IEPopup";
import Notification from "../Notification";
import "./index.scss";
import EmailVerifyNotification from "../EmailVerifyNotification";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";

const Layout = props => {
  const { userinfo, location } = props;

  const shouldVerifyEmail = () => {
    const path = ["/emailverify", "/myaccount"];

    if (path.includes(location.pathname)) {
      return false;
    }
    return userinfo.email && !userinfo.email.includes("@") && !userinfo.verifiedEmail
  }

  return (
    <div 
      className={
        "layout"
        + (props.className ? ` ${props.className}` : "")
      }
    >
      {shouldVerifyEmail() && <EmailVerifyNotification userinfo={userinfo} />}
      {detectIE() && <IEPopup />}
      <Notification />

      <Header />
      <Body>
        {props.children}
      </Body>
      <Footer />
    </div>
  );
};

export default connect(
  state => ({ userinfo: state.userinfo })
)(withRouter(Layout));
