import React from "react";
import { withRouter } from "react-router-dom";
import QueryString from "query-string";
import "./index.scss";
import likeIcon from "../../../Image/new-main-icon-like.svg";
import viewIcon from "../../../Image/new-main-icon-view.svg";
import commentIcon from "../../../Image/new-main-icon-comment.svg";
import { showPopUp } from "../PopUp";
import SignIn from "../SignIn";
import { connect } from "react-redux";
import project3DLabel from "../../../Image/3d-label.svg";


function PublishedItem(props) {
  const { published } = props;

  const handleLoginFor3D = () => {
    if (is2DGame()) {
      replaceToWizAppDetail();
    } else {
      if (isLogin()) {
        replaceToWizAppDetail();
      } else {
        showPopUp(<SignIn />, { mobileFullscreen: true, darkmode: true });
      }
    }
  }

  const is2DGame = () => {
    if (published.icon) {
      return published.icon.includes("wizschool");
    } 
  }

  const isLogin = () => {
    return Object.keys(props.userinfo).length > 0;
  }

  const replaceToWizAppDetail = () => {
    props.history.replace(`?${getQueryString()}`); 
  }

  const getQueryString = () => {
    return QueryString.stringify({
      ...QueryString.parse(props.location.search),
      pId: published.pId
    })
  }

  return (
    <div className="published" onClick={handleLoginFor3D}>
        <div className="published__icon-ratio-wrapper">
          <img
            className="published__icon"
            src={published.icon}
            alt="published-icon"
          />
          {!is2DGame() && <img className="editor-pick__3d-label" src={project3DLabel} alt="3d-label" />}
        </div>
        <div className="published__section">
          <div className="published__title">
            {published.name}
          </div>
          <div className="published__user">
            <img
              className="published__user__icon"
              src={published.user.icon}
              alt="published-user-icon"
            />
            <div className="published__user__name">
              {published.user.name}
            </div>
          </div>
          <div className="published__counts">
            <div className="published__count">
              <img id="like-icon" src={likeIcon} alt="like" />
              {published.likeCount}
            </div>
            <div className="published__count">
              <img id="view-icon" src={viewIcon} alt="view" />
              {published.viewCount}
            </div>
            <div className="published__count">
              <img id="comment-icon" src={commentIcon} alt="comment" />
              {published.commentCount}
            </div>
          </div>
        </div>
    </div>
  )
}

export default connect(
  state => ({
    userinfo: state.userinfo
  })
)(withRouter(PublishedItem));
