import React from "react";
import { Link } from "react-router-dom";
import project3DLabel from "../../../Image/3d-label.svg";
import { is3DProject } from "../../../Page/Builder/Container";

import "./index.scss";

// import playstoreIcon from "../../../Image/google-play-badge@2x.png";

function EditorPick(props) {
  const { editorPick } = props;

  return (
    <div className="editor-pick">
      <div className="editor-pick__thumbnail-ratio-wrapper">
        <Link to={`?pId=${editorPick.pId}`}>
          <img
            className="editor-pick__thumbnail"
            src={editorPick.icon}
            alt="editor-pick-thumbnail"
          />
          {is3DProject(editorPick) && <img className="editor-pick__3d-label" src={project3DLabel} alt="3d-label" />}
        </Link>
        {/* {editorPick.playStoreURL && (
          <a
            className="editor-pick__playstore-link"
            href={editorPick.playStoreURL}
            rel="noopener noreferrer"
            target="_blank"
            onClick={e => {
              e.stopPropagation();
            }}
          >
            <img
              className="editor-pick__playstore-link__icon"
              src={playstoreIcon}
              alt="editor-pick-playstore"
            />
          </a>
        )} */}
      </div>
      <Link
        className="editor-pick__content"
        to={`?pId=${editorPick.pId}`}
      >
        <div className="editor-pick__title">
          {editorPick.project.name}
        </div>
        <div className="editor-pick__creator">
          {editorPick.project.user.name}
        </div>
        <div className="editor-pick__desc">
          {editorPick.description}
        </div>
      </Link>
    </div>
  )
}

export default EditorPick;
