import React from "react";
import * as request from "../../../../Common/Util/HTTPRequest";
import View from "./View";

class Container extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nowPlayings: []
    }
  }
  componentDidMount() {
    this.loadNowPlayings();
  }
  loadNowPlayings() {
    request
      .getGameInfo({ offset: 0, limit: 20 })
      .then(res => res.json())
      .then(result => {
        this.setState({ nowPlayings: result.views });
      })
  }

  render() {
    return (
      <View
        {...this.state}
      />
    );
  }
}

export default Container;
