import React from "react";
import * as request from "../../Util/HTTPRequest";
import EditorPick from "../EditorPick";
import "./index.scss";

class EditorPicks extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editorPicks: []
    }
  }
  componentDidMount() {
    this.loadEditorPicks();
  }

  loadEditorPicks() {
    request
      .getEditorRecommends({ limit: 4 })
      .then(res => res.json())
      .then(editorPicks => {
        this.setState({ editorPicks });
      })
  };

  render() {
    return (
      <div className="editor-picks">
        {this.state.editorPicks.map(editorPick => (
          <EditorPick key={editorPick.id} editorPick={editorPick} />
        ))}
      </div>
    )
  }
}

export default EditorPicks;
