      }`}
    >
      <div className="inspector_property_select_wrapper">
        <select
          className="inspector_property_select"
          value={value}
          onChange={e => {
            onChange(id, e.currentTarget.value);
          }}
          disabled={disabled}
        >
          <option value={null}>선택안함</option>
