            {list.map((l, i) => {
              return (
                <div className="eventEnd1_resultInfo3_item" key={i}>
                  {l[0]}({l[1]})
                </div>
              );
            })}
          </div>
        </div>

        <div className="eventEnd1_resultInfo3">
          <div className="eventEnd1_resultInfo3_titile">
            동일IP 10회이상 참여자 리스트(289명)
          </div>
          <div className="eventEnd1_resultInfo3_list">
            {invalidList.map((l, i) => {
              return (
                <div className="eventEnd1_resultInfo3_item" key={i}>
                  {l[0]}({l[1]})
                </div>
              );
            })}
          </div>
        </div> */}
      </div>
      {/* {isShowNotice && <Notice closeNotice={closeNotice} />} */}
    </Layout>
  );
}
