import React from 'react';
import { FormattedMessage } from "react-intl";
import "./index.scss";

function EmailVerifiedPopup(props) {
  const { isAgeOver14, userName, dismiss } = props;
  return (
    <div className="email-verified">
      <div className="email-verified__title">
        {isAgeOver14
          ? <FormattedMessage id="ID_EMAIL_VERIFY_COMPLETE_TITLE1" /> 
          : <FormattedMessage id="ID_EMAIL_VERIFY_COMPLETE_TITLE2" values={{ name: userName }} /> 
        }
      </div>
      <div className="email-verified__desc">
        {isAgeOver14
          ? <FormattedMessage id="ID_EMAIL_VERIFY_COMPLETE_SUBTITLE1" /> 
          : <FormattedMessage id="ID_EMAIL_VERIFY_COMPLETE_SUBTITLE2" values={{ name: userName }} /> 
        }
      </div>
      <button onClick={() => dismiss()}>
        <FormattedMessage id="ID_CONFIRM" />
      </button>
    </div>
  )
}

export default EmailVerifiedPopup;
