import React, {useState, useEffect} from 'react';
import closeImg from "../../../../Image/btn-close-white.svg";
import "./index.scss";

export default function index(props) {
  const [isHiddenNoticePopup, setIsHiddenNoticePopup] = useState(true);

  useEffect(() => {
    getPopupLocalStage();
  }, [])

  const getPopupLocalStage = () => {
    setIsHiddenNoticePopup(localStorage.getItem('redbrick_notice_popup_show'));
  }

  const onClickClosePopup = () => {
    setIsHiddenNoticePopup(true);
  }

  const onClickDoNotShow = () => {
    localStorage.setItem("redbrick_notice_popup_show", false);
    onClickClosePopup();
  }

  return (
    isHiddenNoticePopup ? 
      null :
      <div className="notice-popup">
        <img
          className="notice-popup__close-btn"
          src={closeImg} 
          alt="close popup" 
          onClick={onClickClosePopup}
        />
        <div className="notice-popup__btn-group__wrap">
          <p className="notice-popup__title">
            {`위즈랩이 레드브릭으로\n변경됩니다.`}
          </p>
          <div className="notice-popup__desc">
            {`3D 스튜디오 등 신규 기능 출시와 홈페이지 \n리뉴얼 관련하여 사전 안내를 드립니다.`}
            <ul className="notice-popup__desc__list one">
              <li>적용 시점: 1월 3일</li>
            </ul>
            <ul className="notice-popup__desc__list two">
              <li>레드브릭으로 서비스명이 변경됩니다.</li>
              <li>3D 빌더에서 3D 스튜디오로 업데이트 되었습니다.</li>
              <li>홈페이지 카테고리명이 변경 될 예정입니다.</li>
            </ul>
          </div>
          <div className="notice-popup__btn-group">
            <button
              type="button"
              onClick={onClickDoNotShow}
            >
              다시 보지 않기
            </button>
            <button
              type="button"
              onClick={onClickClosePopup}
            >
              확인
            </button>
          </div>
        </div>
      </div>
  )
}
