import React from "react";
import "./index.scss";
import edge from "../../../Image/edge-bronze.png";
import { Link } from "react-router-dom";

const UserIconWrapper = props => {
  const { iconSrc, edgeSrc, style, userId, userDefault } = props;

  if (userId) {
    return (
      <Link to={`/userpage/${userId}`} style={{ textDecoration: "none" }}>
        <div
          className={`UserIconWrapper ${
            userDefault ? "Default" : "NotDefault"
          }`}
          style={style}
        >
          <img
            src={edgeSrc ? edgeSrc : edge}
            className="UserIconWrapper_Edge"
            alt="img"
          />
          <img
            className="UserIconWrapper_Icon"
            src={iconSrc}
            alt="img"
            // style={
            //   window.innerWidth <= 1080
            //     ? {
            //         width: "35px",
            //         height: "35px"
            //       }
            //     : {
            //         width: "48px",
            //         height: "48px"
            //       }
            // }
          />
        </div>
      </Link>
    );
  }
  return (
    <div
      className={`UserIconWrapper ${userDefault ? "Default" : "NotDefault"}`}
      style={style}
    >
      <img
        src={edgeSrc ? edgeSrc : edge}
        className="UserIconWrapper_Edge"
        alt="img"
      />
      <img
        className="UserIconWrapper_Icon"
        src={iconSrc}
        alt="img"
        // style={
        //   window.innerWidth <= 1080
        //     ? {
        //         width: "35px",
        //         height: "35px"
        //       }
        //     : {
        //         width: "48px",
        //         height: "48px"
        //       }
        // }
      />
    </div>
  );
};

export default UserIconWrapper;
