import React, { Component } from "react";
import { Link } from "react-router-dom";
import likeIcon from "../../../../../Image/main-icon-like.svg";
import viewIcon from "../../../../../Image/main-icon-view.svg";
import commentIcon from "../../../../../Image/main-icon-comment.svg";
import "./index.scss";

// temp challenge review
export default class View extends Component {
  render() {
    const { challengeProject } = this.props;
    return (
      <Link to={`/challenge/project/${challengeProject.id}/review`}>
        <div className="challenge__project__item">
          <img 
            className="challenge__project__item__thumbnail-image"
            src={challengeProject.project.icon}
            alt="work thumbnail"
          />
          <p className="challenge__project__item__title">{challengeProject.productTitle}</p>
          <p className="challenge__project__item__desc">{challengeProject.teamName}</p>
          <div className="challenge__project__item__info-wrapper">
            <div className="challenge__project__item__info info--like">
              <img src={likeIcon} alt="like"/>
              {challengeProject.project.likeCount}
            </div>
            <div className="challenge__project__item__info info--view">
              <img src={viewIcon} alt="view"/>
              {challengeProject.project.viewCount}
            </div>
            <div className="challenge__project__item__info info--comment">
              <img src={commentIcon} alt="comment"/>
              {challengeProject.project.commentCount}
            </div>
          </div>
        </div>
      </Link>
    )
  }
}
